import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia, { tags } from "typia";
import { IPolicy } from "@ORGANIZATION/PROJECT-api/lib/structures/IPolicy";

/**
 * Archive (soft delete) a specific permission policy by ID, confirm it is deleted, and handle edge cases.
 */
export async function test_soft_delete_permission_policy_and_handle_edge_cases(connection: api.IConnection): Promise<void> {
  // 1. Create a new policy to work with
  const createInput: IPolicy.ICreate = {
    name: `test_policy_${Date.now()}`,
    conditions: '{"allow":true}',
  };
  const created: IPolicy = await api.functional.securityCompliance.policy.post(connection, { body: createInput });
  typia.assert<IPolicy>(created);
  TestValidator.predicate("Created policy should have a UUID")(() => typeof created.id === "string" && created.id.length > 0);

  // 2. Archive (soft delete) the policy
  const deleted: IPolicy = await api.functional.securityCompliance.policy.eraseById(connection, { id: created.id });
  typia.assert<IPolicy>(deleted);
  TestValidator.equals("deleted policy should match the originally created policy", (k) => k === "created_at")({
    ...created,
    created_at: deleted.created_at, // allow timestamp update if soft delete modifies it
  })(deleted);

  // 3. Attempt to delete the same policy again (expect error)
  await TestValidator.httpError("Deleting already archived policy should return HTTP error")
    (404, 410, 400)(
    () => api.functional.securityCompliance.policy.eraseById(connection, { id: created.id })
  );

  // 4. Attempt to delete a non-existent policy (random UUID)
  const randomId: string & tags.Format<"uuid"> = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("Deleting non-existent policy should return HTTP error")
    (404, 410, 400)(
    () => api.functional.securityCompliance.policy.eraseById(connection, { id: randomId })
  );
}

