  const pageLimit = 2;

  // 2. Get first page with limit=2
  const pagedReq: IComplianceReport.IRequest = { ...noFilterReq };
  // Pagination settings would normally be in the DTO or prop, but not present here. Simulate as if limit is always 2 on API side
  const pagedRes: IPageIComplianceReport.ISummary = await api.functional.securityCompliance.complianceReport.patch(connection, {
    body: pagedReq,
  });
