import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IComplianceReport } from "@ORGANIZATION/PROJECT-api/lib/structures/IComplianceReport";
import typia, { tags } from "typia";

export async function test_update_compliance_report_with_valid_and_invalid_id(
  connection: api.IConnection
): Promise<void> {
  // 1. Create a compliance report for update scenario
  const createInput: IComplianceReport.ICreate = {
    report_type: "GDPR_EXPORT",
    status: "pending",
    // user_id and organization_id are optional, omit for simplicity
  };
  const created: IComplianceReport = await api.functional.securityCompliance.complianceReport.post(
    connection,
    { body: createInput }
  );
  typia.assert(created);

  // 2. Update the compliance report's permitted fields
  const updateInput: IComplianceReport.IUpdate = {
    status: "published",
    published_at: new Date().toISOString() as string & tags.Format<"date-time">,
  };
  const updated: IComplianceReport = await api.functional.securityCompliance.complianceReport.putById(
    connection,
    {
      id: created.id,
      body: updateInput,
    }
  );
  typia.assert(updated);
  TestValidator.equals("updated.status")<string>(updateInput.status!)(updated.status);
  TestValidator.equals("updated.published_at")<string | undefined>(updateInput.published_at)(updated.published_at);
  // Only status and published_at should be changed; id, report_type should not change
  TestValidator.equals("report_type unchanged")<string>(created.report_type)(updated.report_type);
  TestValidator.equals("id unchanged")<string>(created.id)(updated.id);

  // 3. Attempt to update a non-existent compliance report (should return 404)
  const randomId = typia.random<string & tags.Format<"uuid">>();
  await TestValidator.httpError("update non-existent compliance report")(404)(async () =>
    api.functional.securityCompliance.complianceReport.putById(
      connection,
      { id: randomId, body: updateInput }
    )
  );

  // 4. Invalid update (invalid status and published_at format)
  const invalidUpdate: IComplianceReport.IUpdate = {
    status: 123 as any, // status expects string, provide number
    published_at: "not-a-date" as any, // published_at should be date-time
  };
  await TestValidator.httpError("invalid update payload should fail")(400, 422)(async () =>
    api.functional.securityCompliance.complianceReport.putById(
      connection,
      { id: created.id, body: invalidUpdate }
    )
  );
}

