import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IDataRetention } from "@ORGANIZATION/PROJECT-api/lib/structures/IDataRetention";
import { IPageIDataRetention } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIDataRetention";

// Helper: Check all results match given filter condition
function allMatch<T>(arr: T[], pred: (t: T) => boolean): boolean {
  return arr.every(pred);
}

export async function test_list_data_retention_policies_with_filtering_and_edge_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. List with no filters
  const res1 = await api.functional.securityCompliance.dataRetention.patch(connection, { body: {} });
  typia.assert<IPageIDataRetention.ISummary>(res1);
  TestValidator.predicate("data present")(() => Array.isArray(res1.data));

  // If there's no data, skip downstream checks, else use for filters
  const anyData = res1.data.length > 0;
  let orgId: string | undefined = undefined;
  let entity: string | undefined = undefined;
  const legalHold = true;

  if (anyData) {
    orgId = res1.data.find(d => d.organization_id)?.organization_id ?? undefined;
    entity = res1.data[0].entity_type;
  }

  // 2. Filter by organization_id (if present)
  if (orgId) {
    const res2 = await api.functional.securityCompliance.dataRetention.patch(connection, { body: { organization_id: orgId } });
    typia.assert<IPageIDataRetention.ISummary>(res2);
    TestValidator.predicate("all have org_id")(() => allMatch(res2.data, d => d.organization_id === orgId));
  }

  // 3. Filter by entity_type
  if (entity) {
    const res3 = await api.functional.securityCompliance.dataRetention.patch(connection, { body: { entity_type: entity } });
    typia.assert<IPageIDataRetention.ISummary>(res3);
    TestValidator.predicate("all match entity_type")(() => allMatch(res3.data, d => d.entity_type === entity));
  }

  // 4. Filter by is_legal_hold=true
  const res4 = await api.functional.securityCompliance.dataRetention.patch(connection, { body: { is_legal_hold: legalHold } });
  typia.assert<IPageIDataRetention.ISummary>(res4);
  TestValidator.predicate("all have is_legal_hold true")(() => allMatch(res4.data, d => d.is_legal_hold === legalHold));

  // 5. Combine filters (if orgId, entity present)
  if (orgId && entity) {
    const res5 = await api.functional.securityCompliance.dataRetention.patch(connection, { body: { organization_id: orgId, entity_type: entity, is_legal_hold: legalHold } });
    typia.assert<IPageIDataRetention.ISummary>(res5);
    TestValidator.predicate("intersection filters correct")(() => allMatch(res5.data, d =>
      d.organization_id === orgId && d.entity_type === entity && d.is_legal_hold === legalHold
    ));
  }

  // 6. Search with impossible filter (should yield empty array)
  const res6 = await api.functional.securityCompliance.dataRetention.patch(connection, {
    body: { organization_id: "00000000-0000-0000-0000-000000000000", entity_type: "nonexistent", is_legal_hold: false },
  });
  typia.assert<IPageIDataRetention.ISummary>(res6);
  TestValidator.equals("empty data on impossible filter")([] as IDataRetention.ISummary[])(res6.data);

  // 7. Test pagination: if records > limit, fetch next page
  if (res1.pagination.records > res1.pagination.limit) {
    const nextPageRes = await api.functional.securityCompliance.dataRetention.patch(connection, {
      body: { },
    });
    // (Assumes system default is page 1; there's no explicit 'page' param)
    typia.assert<IPageIDataRetention.ISummary>(nextPageRes);
    TestValidator.predicate("pagination structure valid")(() =>
      typeof nextPageRes.pagination.current === "number" &&
      typeof nextPageRes.pagination.records === "number"
    );
  }

  // 8. Invalid filters (malformed UUID)
  await TestValidator.httpError("malformed UUID filters cause error")(400)(async () =>
    api.functional.securityCompliance.dataRetention.patch(connection, { body: { organization_id: "not-a-uuid" } }),
  );

  // 9. Invalid filters (wrong data types)
  await TestValidator.httpError("bad data types cause error")(400)(async () =>
    api.functional.securityCompliance.dataRetention.patch(connection, { body: { is_legal_hold: "badval" as any } }),
  );
}

