  TestValidator.predicate("data is array")(() => Array.isArray(allResult.data));

  // If there is at least one archive entry, use its properties for further filtering
  const sample: IEvidenceArchive | undefined = allResult.data[0];

  // 2. Filter by evidence_type (if available)
  if (sample) {
    const typeResult = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
      body: { evidence_type: sample.evidence_type, limit: 10 },
    });
    typia.assert<IPageIEvidenceArchive>(typeResult);
