    const checksumResult = await api.functional.securityCompliance.evidenceArchive.patch(connection, {
      body: { checksum: sample.checksum },
    });
    typia.assert<IPageIEvidenceArchive>(checksumResult);
    for (const ev of checksumResult.data) {
      TestValidator.equals("checksum filter matches")(sample.checksum)(ev.checksum);
    }
  }

