import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IPageIApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiClients";
import typia from "typia";

export async function test_list_api_clients_with_filtering_and_pagination(connection: api.IConnection): Promise<void> {
  // Search by client_name (assuming at least one client named 'TestAppOne')
  const searchByName: IApiClients.IRequest = { client_name: "TestAppOne" };
  const resByName = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: searchByName });
  typia.assert<IPageIApiClients>(resByName);
  TestValidator.predicate("all client names match")(() => resByName.data.every(c => c.client_name === "TestAppOne"));

  // Filter by is_active true
  const searchActive: IApiClients.IRequest = { is_active: true };
  const resActive = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: searchActive });
  typia.assert<IPageIApiClients>(resActive);
  TestValidator.predicate("all clients are active")(() => resActive.data.every(c => c.is_active === true));

  // Filter by is_active false
  const searchInactive: IApiClients.IRequest = { is_active: false };
  const resInactive = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: searchInactive });
  typia.assert<IPageIApiClients>(resInactive);
  TestValidator.predicate("all clients are inactive")(() => resInactive.data.every(c => c.is_active === false));

  // Filter by contact_email (assume known test email)
  const testEmail = "noreply-test@domain.io";
  const searchByEmail: IApiClients.IRequest = { contact_email: testEmail };
  const resByEmail = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: searchByEmail });
  typia.assert<IPageIApiClients>(resByEmail);
  TestValidator.predicate("all clients have given email")(() => resByEmail.data.every(c => c.contact_email === testEmail));

  // Pagination test: page 1, limit 2
  const pageLimit: IApiClients.IRequest = { page: 1, limit: 2 };
  const pgRes1 = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: pageLimit });
  typia.assert<IPageIApiClients>(pgRes1);
  TestValidator.predicate("limit respected")(() => pgRes1.data.length <= 2);

  // If more than 2 records, query page 2
  if (pgRes1.pagination.records > 2) {
    const page2Limit: IApiClients.IRequest = { page: 2, limit: 2 };
    const pgRes2 = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: page2Limit });
    typia.assert<IPageIApiClients>(pgRes2);
    TestValidator.predicate("page 2 offset works")(() => {
      if (pgRes2.data.length === 0) return true;
      // Should not be duplicate of first page (id compare)
      return pgRes1.data.every(d1 => pgRes2.data.every(d2 => d1.id !== d2.id));
    });
  }

  // Edge case: filter for non-existent client_name
  const nonExistent: IApiClients.IRequest = { client_name: "DefinitelyNoSuchClientAppName1234" };
  const resNone = await api.functional.apiOmnichannel.apiClients.patch(connection, { body: nonExistent });
  typia.assert<IPageIApiClients>(resNone);
  TestValidator.equals("no record result")<IApiClients[]>([])(resNone.data);

  // Invalid page param (negative)
  const invalidPage: IApiClients.IRequest = { page: -1 };
  await TestValidator.httpError("invalid negative page")(400)(() => api.functional.apiOmnichannel.apiClients.patch(connection, { body: invalidPage }));

  // Invalid limit param (zero)
  const invalidLimit: IApiClients.IRequest = { limit: 0 };
  await TestValidator.httpError("invalid zero limit")(400)(() => api.functional.apiOmnichannel.apiClients.patch(connection, { body: invalidLimit }));

  // Non-integer page (string, should error, TS will block but for API test):
  const invalidNonIntPage = { ...(pageLimit as any), page: "abc" };
  await TestValidator.httpError("non-integer page string")(400)(() => api.functional.apiOmnichannel.apiClients.patch(connection, { body: invalidNonIntPage }));
}

