  );

  // 6. Duplicate client_name (should fail)
  const duplicateClient: IApiClients.ICreate = {
    client_name: validInput.client_name,
    contact_email: `duplicate${Date.now()}@example.com`,
  };
  await TestValidator.httpError("duplicate client_name")(409, 400)(() =>
    api.functional.apiOmnichannel.apiClients.post(connection, {
      body: duplicateClient,
    })
  );

  // 7. Reserved client_name (e.g., "admin")
  const reservedClient: IApiClients.ICreate = {
    client_name: "admin",
    contact_email: `reserved${Date.now()}@example.com`,
  };
  await TestValidator.httpError("reserved client_name")(400, 403)(() =>
    api.functional.apiOmnichannel.apiClients.post(connection, {
      body: reservedClient,
    })
  );

  // 8. Try minimal valid fields (no public_key, no description)
  const minimalInput: IApiClients.ICreate = {
    client_name: `minimal-client-${Date.now()}`,
    contact_email: `minimal${Date.now()}@example.com`,
  };
  const minimalOutput = await api.functional.apiOmnichannel.apiClients.post(connection, {
    body: minimalInput,
  });
  typia.assert<IApiClients>(minimalOutput);
  TestValidator.equals("client_name")(minimalInput.client_name)(minimalOutput.client_name);
  TestValidator.equals("contact_email")(minimalInput.contact_email)(minimalOutput.contact_email);
  TestValidator.predicate("is_active is true for minimal input")(() => minimalOutput.is_active === true);
}
