import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import typia from "typia";

export async function test_update_api_client_metadata_and_handle_errors(connection: api.IConnection): Promise<void> {
    // 1. Create initial API client
    const createInput: IApiClients.ICreate = {
        client_name: "partner-app-alpha",
        contact_email: "admin@example.org",
        public_key: "-----BEGIN PUBLIC KEY-----\nMIIBIjANBgkqhki...END PUBLIC KEY-----\n",
        description: "Test integration client for PUT update"
    };
    const created = await api.functional.apiOmnichannel.apiClients.post(connection, createInput);
    typia.assert<IApiClients>(created);

    // 2. Update all mutable fields
    const updateInput: IApiClients.IUpdate = {
        description: "Integration client description updated",
        contact_email: "ops-updated@example.org",
        public_key: "-----BEGIN PUBLIC KEY-----\nNEWKEYEXAMPLE==\nEND PUBLIC KEY-----\n",
        is_active: false
    };
    const updated = await api.functional.apiOmnichannel.apiClients.put(connection, created.id, updateInput);
    typia.assert<IApiClients>(updated);
    // Ensure core immutable fields have not changed
    TestValidator.equals("client_name unchanged")<string>(created.client_name)(updated.client_name);
    TestValidator.equals("id unchanged")<string>(created.id)(updated.id);
    // Validate that updates are persisted
    TestValidator.equals("description updated")<string | null>(updateInput.description!)(updated.description);
    TestValidator.equals("contact_email updated")<string>(updateInput.contact_email!)(updated.contact_email);
    TestValidator.equals("public_key updated")<string | null>(updateInput.public_key!)(updated.public_key);
    TestValidator.equals("status updated")<boolean>(updateInput.is_active!)(updated.is_active);

    // 3. Attempt to update with a non-existent id (expect 404 or error)
    await TestValidator.httpError("update with non-existent id should 404")(404)(async () => {
        await api.functional.apiOmnichannel.apiClients.put(connection, "b60c5599-0000-0000-baaa-ffaaffffffff", updateInput);
    });

    // 4. Attempt to update with invalid (malformed) id format
    await TestValidator.httpError("update with invalid/malformed UUID should error")(400, 422)(async () => {
        await api.functional.apiOmnichannel.apiClients.put(connection, "non-uuid-id", updateInput);
    });

    // 5. Attempt to update with empty payload (invalid update)
    await TestValidator.httpError("empty update payload should error")(400, 422)(async () => {
        await api.functional.apiOmnichannel.apiClients.put(connection, created.id, {});
    });

    // 6. Malformed field: wrong type for is_active
    await TestValidator.httpError("malformed payload: is_active as string should error")(400, 422)(async () => {
        // @ts-expect-error simulated for test only
        await api.functional.apiOmnichannel.apiClients.put(connection, created.id, {
            is_active: "not-a-boolean"
        });
    });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import typia from "typia";
import { v4 as uuidv4 } from "uuid";

export async function test_soft_delete_api_client_and_handle_invalid_id(
  connection: api.IConnection,
): Promise<void> {
  // 1. Prepare input for API client creation
  const createInput: IApiClients.ICreate = {
    client_name: `TestClient_${Date.now()}`,
    contact_email: `testclient_${Date.now()}@mail.com`,
    public_key: "test-public-key",
    description: "Test client for soft delete E2E test"
  };

  // 2. Create API client
  const created: IApiClients = await api.functional.api-omnichannel.apiClients.post(
    connection,
    createInput,
  );
  typia.assert<IApiClients>(created);

  // Validate basic creation
  TestValidator.predicate("created client is active")(() => created.is_active === true);
  TestValidator.equals("created client has null deleted_at")<IApiClients>(created)({ ...created, deleted_at: null });

  // 3. Soft-delete (deactivate) the API client
  const deleted: IApiClients = await api.functional.api-omnichannel.apiClients.delete(
    connection,
    created.id
  );
  typia.assert<IApiClients>(deleted);

  // 4. Validate response: deleted_at is set, is_active is false
  TestValidator.predicate("deleted client is not active")(() => deleted.is_active === false);
  TestValidator.predicate("deleted_at timestamp is set after deletion")(() => typeof deleted.deleted_at === "string" && deleted.deleted_at.length > 0);

  // 5. Negative test: soft delete using an invalid/random UUID
  const invalidId = uuidv4();
  await TestValidator.httpError("delete with invalid client id should fail")(404)(
    () => api.functional.api-omnichannel.apiClients.delete(connection, invalidId)
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiClients as IApiClientsNs } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IApiToken as IApiTokenNs } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiTokens as IApiTokensNs } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IApiTokens as IApiTokensNs2 } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IApiTokens as IApiTokensNs3 } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IPageIApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiTokens";
import { IPageIApiTokens as IPageIApiTokensNs } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiTokens";
import { IApiTokens as IApiTokensNs4 } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IApiToken as IApiTokenNs2 } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiClients as IApiClientsNs2 } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import typia from "typia";

export async function test_api_list_and_search_api_tokens_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. Register a new API client
  const clientInput: IApiClientsNs.ICreate = {
    client_name: "test_client_" + Math.random().toString(36).substring(2,8),
    contact_email: `test-${Date.now()}@mail.com`,
    public_key: "testPublicKey",
    description: "Test API client"
  };
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, clientInput);
  typia.assert<IApiClients>(client);

  // 2. Create several API tokens linked to the client
  const now = new Date();
  const tokens: IApiToken[] = [];
  for (let i = 0; i < 4; ++i) {
    const issued_at = new Date(now.getTime() - i * 1000 * 60 * 60); // previous hours
    const expires_at = new Date(now.getTime() + (2 + i) * 1000 * 60 * 60);
    const tokenInput: IApiTokenNs.ICreate = {
      api_client_id: client.id,
      scope: `test-scope-${i % 2 ? "read" : "write"}`,
      issued_at: issued_at.toISOString(),
      expires_at: expires_at.toISOString()
    };
    const token = await api.functional.apiOmnichannel.apiTokens.post(connection, tokenInput);
    typia.assert<IApiToken>(token);
    tokens.push(token);
  }

  // 3. List tokens without filters
  let req: any = {};
  let res: IPageIApiTokens = await api.functional.apiOmnichannel.apiTokens.patch(connection, req);
  typia.assert<IPageIApiTokens>(res);
  TestValidator.predicate("list returns array")(Array.isArray(res.data) && res.data.length >= tokens.length);
  TestValidator.predicate("pagination meta present")(
    typeof res.pagination.current === "number" &&
    typeof res.pagination.limit === "number"
  );

  // 4. List tokens filtered by client
  req = { api_client_id: client.id };
  res = await api.functional.apiOmnichannel.apiTokens.patch(connection, req);
  typia.assert<IPageIApiTokens>(res);
  TestValidator.predicate("all tokens are for client")(
    res.data.every(t => t.api_client_id === client.id)
  );

  // 5. List tokens filtered by status (active)
  req = { active: true };
  res = await api.functional.apiOmnichannel.apiTokens.patch(connection, req);
  typia.assert<IPageIApiTokens>(res);
  TestValidator.predicate("active tokens are marked active")(
    res.data.every(t => t.active === true)
  );

  // 6. Filter by issued_at/expires_at range (past window - expect at most exact matches, possibly none)
  const pastDate = new Date(now.getTime() - 10 * 24 * 3600000).toISOString();
  req = { issued_from: pastDate, issued_to: pastDate };
  res = await api.functional.apiOmnichannel.apiTokens.patch(connection, req);
  typia.assert<IPageIApiTokens>(res);
  // Should be empty or at most edge case matches
  TestValidator.equals("empty data for far past issued search")([] as IApiTokens[])(res.data);

  // 7. Test pagination params (page, limit)
  req = { page: 1, limit: 2 };
  res = await api.functional.apiOmnichannel.apiTokens.patch(connection, req);
  typia.assert<IPageIApiTokens>(res);
  TestValidator.predicate("pagination enforced")(res.data.length <= 2 && res.pagination.current === 1);

  // 8. Extreme/invalid pagination and bad filter values
  // Extreme page
  req = { page: 1000, limit: 2 };
  res = await api.functional.apiOmnichannel.apiTokens.patch(connection, req);
  typia.assert<IPageIApiTokens>(res);
  TestValidator.predicate("out of range page returns empty data")(Array.isArray(res.data) && res.data.length === 0);

  // Invalid negative values
  await TestValidator.httpError("negative limit when paginating")(400)(() => api.functional.apiOmnichannel.apiTokens.patch(connection, { limit: -1 }));
  await TestValidator.httpError("zero page when paginating")(400)(() => api.functional.apiOmnichannel.apiTokens.patch(connection, { page: 0 }));

  // Bad uuid
  await TestValidator.httpError("bad uuid filter")(400)(() => api.functional.apiOmnichannel.apiTokens.patch(connection, { api_client_id: "not-a-uuid" }));
  // Unexpected value for filter
  await TestValidator.httpError("unexpected value for active")(400)(() => api.functional.apiOmnichannel.apiTokens.patch(connection, { active: "not-a-boolean" as any }));
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiTokens } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiTokens";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import typia from "typia";

export async function test_api_get_api_token_by_id_with_valid_and_invalid_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create API client
  const clientInput: IApiClients.ICreate = {
    client_name: "test-client-for-token-retrieval",
    contact_email: `token_test_${Date.now()}@e2e.test`,
    public_key: "test-public-key",
    description: "Client used for token retrieval test",
  };
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(connection, clientInput);
  typia.assert<IApiClients>(client);
  TestValidator.predicate("Client active")(client.is_active === true);

  // 2. Create API token for this client
  const now = new Date();
  const oneDayLater = new Date(now.getTime() + 24 * 60 * 60 * 1000);
  const tokenInput: IApiToken.ICreate = {
    api_client_id: client.id,
    scope: "e2e_read_write",
    issued_at: now.toISOString(),
    expires_at: oneDayLater.toISOString(),
  };

  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(connection, tokenInput);
  typia.assert<IApiToken>(token);
  TestValidator.equals("issued client ID")(token.api_client_id)(client.id);
  TestValidator.equals("token scope")(token.scope)("e2e_read_write");

  // 3. Fetch the token using GET by ID (positive case)
  const fetched: IApiTokens = await api.functional.apiOmnichannel.apiTokens.get(connection, token.id);
  typia.assert<IApiTokens>(fetched);
  TestValidator.equals("fetched token record matches creation", (key: string) => key === "client_name")(<IApiTokens>{
    ...token,
    // client_name is response-only, may not match exactly (exclude from strict compare)
  })(fetched);

  // 4. Fetch non-existent token by random UUID (negative case)
  const fakeId = "11111111-1111-4111-8111-111111111111"; // unlikely to exist
  await TestValidator.httpError("get non-existent api token")(404)(
    () => api.functional.apiOmnichannel.apiTokens.get(connection, fakeId),
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import typia from "typia";

export async function test_api_create_api_token_for_client_and_check_constraints(connection: api.IConnection): Promise<void> {
    // 1. Register a new API client
    const clientInput: IApiClients.ICreate = {
        client_name: `E2E_TEST_CLIENT_${Date.now()}`,
        contact_email: `e2e.client${Date.now()}@test.com`,
        public_key: "MIIBIjANBgkqhkiG9w0BAQEFAAOCAQ8AMIIBCgKCAQEApQ==", // example
        description: "Test client registration for E2E"
    };
    const registeredClient = await api.functional.apiOmnichannel.apiClients.post(connection, clientInput);
    typia.assert<IApiClients>(registeredClient);
    TestValidator.equals("Registered client matches input")<IApiClients>({
        client_name: clientInput.client_name,
        contact_email: clientInput.contact_email,
        is_active: true
    })(registeredClient);
    TestValidator.predicate("New client has a non-empty uuid")(() => typeof registeredClient.id === "string" && registeredClient.id.length > 0);

    // 2. Issue a new API token for this client
    const now = new Date();
    const expiresAt = new Date(now.getTime() + 3600 * 1000); // 1 hour later
    const tokenInput: IApiToken.ICreate = {
        api_client_id: registeredClient.id,
        scope: "read:all",
        issued_at: now.toISOString(),
        expires_at: expiresAt.toISOString(),
    };
    const token = await api.functional.apiOmnichannel.apiTokens.post(connection, tokenInput);
    typia.assert<IApiToken>(token);
    TestValidator.equals("Token linked to client")<string>(tokenInput.api_client_id)(token.api_client_id);
    TestValidator.predicate("Token uuid non-empty")(() => typeof token.id === "string" && token.id.length > 0);
    TestValidator.predicate("Token has value")(() => typeof token.token_value === "string" && token.token_value.length > 0);
    TestValidator.equals("Token scope correct")<string>(tokenInput.scope)(token.scope);

    // 3. Duplicate token test (depends on business rules)
    await TestValidator.httpError("Duplicate token issue should fail or handle gracefully")(400, 409)(async () => {
        await api.functional.apiOmnichannel.apiTokens.post(connection, tokenInput);
    });

    // 4. Negative test: missing required fields
    await TestValidator.httpError("Missing required api_client_id") (400, 422)(async () => {
        await api.functional.apiOmnichannel.apiTokens.post(connection, {
            // @ts-expect-error
            scope: "bad-missing-id",
            issued_at: now.toISOString(),
            expires_at: expiresAt.toISOString(),
        });
    });
    await TestValidator.httpError("Issued_at after expires_at should fail validation") (400, 422)(async () => {
        await api.functional.apiOmnichannel.apiTokens.post(connection, {
            api_client_id: registeredClient.id,
            scope: "read:all",
            issued_at: expiresAt.toISOString(), // intentionally after
            expires_at: now.toISOString()
        });
    });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import typia from "typia";

export async function test_update_api_token_metadata_with_valid_and_invalid_id(connection: api.IConnection): Promise<void> {
  // 1. Create a new API Client
  const clientInput: IApiClients.ICreate = {
    client_name: "test-app-automation",
    contact_email: `testclient+${Date.now()}@example.com`,
    public_key: "test-public-key",
    description: "Test automation client for API token update E2E"
  };
  const client = await api.functional.apiOmnichannel.apiClients.post(connection, clientInput);
  typia.assert<IApiClients>(client);

  // 2. Create an API Token for that client
  const now = new Date();
  const futureDate = new Date(now.getTime() + 1000 * 60 * 60 * 24 * 10); // 10 days in the future
  const tokenInput: IApiToken.ICreate = {
    api_client_id: client.id,
    scope: "read write",
    issued_at: now.toISOString(),
    expires_at: futureDate.toISOString(),
  };
  const token = await api.functional.apiOmnichannel.apiTokens.post(connection, tokenInput);
  typia.assert<IApiToken>(token);

  // 3. Update the token's metadata (scope and expires_at)
  const newScope = "read only";
  const newExpiresAt = new Date(now.getTime() + 1000 * 60 * 60 * 24 * 20).toISOString(); // 20 days in the future
  const updateInput: IApiToken.IUpdate = {
    scope: newScope,
    expires_at: newExpiresAt,
  };
  const updatedToken = await api.functional.apiOmnichannel.apiTokens.put(connection, token.id, updateInput);
  typia.assert<IApiToken>(updatedToken);
  TestValidator.equals("updated token id")(token.id)(updatedToken.id);
  TestValidator.equals("updated token scope")(newScope)(updatedToken.scope);
  TestValidator.equals("updated token expires_at")(newExpiresAt)(updatedToken.expires_at);

  // 4. Negative: Try updating a non-existent token ID (should error)
  const fakeId = "00000000-0000-4000-8000-000000000001";
  await TestValidator.httpError("update non-existent token id")(404, 400)(async () => {
    await api.functional.apiOmnichannel.apiTokens.put(connection, fakeId, updateInput);
  });

  // 5. Negative: Try updating with invalid fields (e.g. empty scope)
  const invalidUpdate1: IApiToken.IUpdate = {
    scope: "",
    expires_at: newExpiresAt
  };
  await TestValidator.httpError("update token with empty scope")(400, 422)(async () => {
    await api.functional.apiOmnichannel.apiTokens.put(connection, token.id, invalidUpdate1);
  });

  // 6. Negative: Try updating with expired date in the past
  const expiredDate = new Date(now.getTime() - 1000 * 60 * 60 * 24).toISOString(); // 1 day in the past
  const invalidUpdate2: IApiToken.IUpdate = {
    expires_at: expiredDate
  };
  await TestValidator.httpError("update token with expired date")(400, 422)(async () => {
    await api.functional.apiOmnichannel.apiTokens.put(connection, token.id, invalidUpdate2);
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiClients } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiClients as IApiClientsType } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiClients";
import { IApiToken } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import { IApiToken as IApiTokenType } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiToken";
import typia from "typia";

export async function test_api_revoke_token_and_error_handling(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create an API client
  const clientInput: IApiClientsType.ICreate = {
    client_name: "TestE2EClient",
    contact_email: `e2e+client${Date.now()}@example.com`,
    public_key: "dummy-public-key",
    description: "E2E test client setup."
  };
  const client: IApiClients = await api.functional.apiOmnichannel.apiClients.post(
    connection,
    clientInput
  );
  typia.assert<IApiClients>(client);
  TestValidator.predicate("New client must be active")(() => client.is_active === true);

  // 2. Create an API token for that client
  const issuedAt = new Date().toISOString();
  const expiresAt = new Date(Date.now() + 1000 * 60 * 60 * 24).toISOString();
  const tokenInput: IApiTokenType.ICreate = {
    api_client_id: client.id,
    scope: "read:all",
    issued_at: issuedAt,
    expires_at: expiresAt
  };
  const token: IApiToken = await api.functional.apiOmnichannel.apiTokens.post(
    connection,
    tokenInput
  );
  typia.assert<IApiToken>(token);
  TestValidator.equals("token client_id matches")(token.api_client_id)(client.id);

  // 3. Revoke the token by id
  const revoked: IApiToken = await api.functional.apiOmnichannel.apiTokens.delete(
    connection,
    token.id
  );
  typia.assert<IApiToken>(revoked);
  TestValidator.predicate("revoked_at is set")(
    () => typeof revoked.revoked_at === "string" && revoked.revoked_at.length > 0
  );
  TestValidator.equals("revoked id matches")(revoked.id)(token.id);

  // 4. Attempt to revoke again: should yield error
  await TestValidator.httpError("double-revoke error")(404)(async () => {
    await api.functional.apiOmnichannel.apiTokens.delete(connection, token.id);
  });

  // 5. Attempt to revoke a non-existent id (random UUID)
  const fakeId = "00000000-0000-4000-8000-000000000000";
  await TestValidator.httpError("invalid id error")(404)(async () => {
    await api.functional.apiOmnichannel.apiTokens.delete(connection, fakeId);
  });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiAudit";
import typia from "typia";
import { IPageIApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIApiAudit";

export async function test_api_list_search_api_audit_entries_with_varied_filters(connection: api.IConnection): Promise<void> {
    // Step 1: Create several distinct API audit entries
    const now = new Date();
    const auditInputs: IApiAudit.ICreate[] = [
        {
            api_client_id: "00000000-0000-0000-0000-000000000001",
            api_token_id: "10000000-0000-0000-0000-000000000001",
            integration_channel_id: "20000000-0000-0000-0000-000000000001",
            endpoint: "/test/order",
            http_method: "POST",
            actor_id: "30000000-0000-0000-0000-000000000001",
            decision_log_id: "decision-1",
            request_hash: "reqhash1",
            response_hash: "reshash1",
            is_success: true,
            status_code: 200,
            created_at: new Date(now.getTime() - 10000).toISOString(),
        },
        {
            api_client_id: "00000000-0000-0000-0000-000000000002",
            api_token_id: "10000000-0000-0000-0000-000000000002",
            integration_channel_id: "20000000-0000-0000-0000-000000000002",
            endpoint: "/test/order",
            http_method: "POST",
            actor_id: "30000000-0000-0000-0000-000000000002",
            decision_log_id: "decision-2",
            request_hash: "reqhash2",
            response_hash: "reshash2",
            is_success: false,
            status_code: 403,
            created_at: new Date(now.getTime() - 9000).toISOString(),
        },
        {
            api_client_id: "00000000-0000-0000-0000-000000000001",
            api_token_id: "10000000-0000-0000-0000-000000000003",
            integration_channel_id: "20000000-0000-0000-0000-000000000001",
            endpoint: "/test/inventory",
            http_method: "GET",
            actor_id: "30000000-0000-0000-0000-000000000001",
            decision_log_id: "decision-3",
            request_hash: "reqhash3",
            response_hash: "reshash3",
            is_success: true,
            status_code: 200,
            created_at: new Date(now.getTime() - 8000).toISOString(),
        }
    ];

    const created: IApiAudit[] = [];
    for (const input of auditInputs) {
        const entry = await api.functional.apiOmnichannel.apiAudits.post(connection, input);
        typia.assert<IApiAudit>(entry);
        created.push(entry);
    }

    // Step 2: Search all (no filters, base pagination)
    let result = await api.functional.apiOmnichannel.apiAudits.patch(connection, {});
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("all present")(() => created.every(e => result.data.some(d => d.id === e.id)));

    // Step 3: Filter by api_client_id
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { api_client_id: auditInputs[0].api_client_id });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("api_client_id filter")(() => result.data.every(d => d.api_client_id === auditInputs[0].api_client_id));

    // Filter by actor_id
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { actor_id: auditInputs[1].actor_id });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("actor_id filter")(() => result.data.every(d => d.actor_id === auditInputs[1].actor_id));

    // Filter by http_method
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { http_method: "POST" });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("http_method filter")(() => result.data.every(d => d.http_method === "POST"));

    // Filter by status_code
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { status_code: 200 });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("status_code filter")(() => result.data.every(d => d.status_code === 200));

    // Filter by is_success true
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { is_success: true });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("is_success true filter")(() => result.data.every(d => d.is_success === true));

    // Filter by endpoint substring (simulate by direct, since only exact match supported per schema)
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { endpoint: "/test/order" });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("endpoint filter")(() => result.data.every(d => d.endpoint === "/test/order"));

    // Filter by created_at range
    const from = new Date(now.getTime() - 9500).toISOString();
    const to = new Date(now.getTime() - 8000).toISOString();
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { created_from: from, created_to: to });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("created_at range filter")( () => result.data.every(d => d.created_at >= from && d.created_at <= to) );

    // Combine multiple filters
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, {
        api_client_id: auditInputs[0].api_client_id,
        endpoint: "/test/order",
        http_method: "POST",
        is_success: true,
        status_code: 200
    });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.predicate("multi-filter")(() => result.data.every(d => d.api_client_id === auditInputs[0].api_client_id && d.endpoint === "/test/order" && d.http_method === "POST" && d.is_success === true && d.status_code === 200));

    // Step 5: Pagination - set limit=2
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { limit: 2, page: 1 });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.equals("pagination: limit=2")(2)(result.data.length <= 2 ? result.data.length : 2);
    TestValidator.equals("pagination current page")(1)(result.pagination.current);

    // Step 6: Negative filters - impossible UUID and out of range status_code
    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { api_client_id: "ffffffff-ffff-ffff-ffff-ffffffffffff" });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.equals("impossible uuid")(0)(result.data.length);

    result = await api.functional.apiOmnichannel.apiAudits.patch(connection, { status_code: 999 });
    typia.assert<IPageIApiAudit>(result);
    TestValidator.equals("no such status_code")(0)(result.data.length);

    // Step 7: Invalid filter input (status_code as string) - should error
    await TestValidator.httpError("invalid status_code type")(400)(async () => {
        // TypeScript will block a wrong type, so force via 'as any'
        await api.functional.apiOmnichannel.apiAudits.patch(connection, { status_code: "not-a-number" as any });
    });
}

