import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IApiAudit } from "@ORGANIZATION/PROJECT-api/lib/structures/IApiAudit";

export async function test_update_api_audit_entry_by_id_and_edge_cases(
  connection: api.IConnection
): Promise<void> {
  // Step 1: Create a valid audit entry
  const now = new Date();
  const baseCreate: IApiAudit.ICreate = {
    api_client_id: "c0a80100-0000-4000-a000-000000000001",
    api_token_id: "c0a80100-0000-4000-a000-000000000002",
    integration_channel_id: "c0a80100-0000-4000-a000-000000000003",
    endpoint: "/apiOmnichannel/test",
    http_method: "POST",
    actor_id: "c0a80100-0000-4000-a000-000000000004",
    decision_log_id: "test-decision-id",
    request_hash: "reqhash123456789",
    response_hash: "reshash987654321",
    is_success: true,
    status_code: 201,
    created_at: now.toISOString(),
  };

  const created = await api.functional.apiOmnichannel.apiAudits.post(
    connection,
    baseCreate
  );
  typia.assert<IApiAudit>(created);
  TestValidator.equals("Create: endpoint field")<string>(baseCreate.endpoint)(created.endpoint);

  // Step 2: Update select fields of the audit entry
  const updateBody: IApiAudit.IUpdate = {
    api_client_id: created.api_client_id,
    api_token_id: created.api_token_id,
    integration_channel_id: created.integration_channel_id,
    endpoint: created.endpoint,
    http_method: created.http_method,
    actor_id: created.actor_id,
    decision_log_id: created.decision_log_id,
    request_hash: created.request_hash,
    response_hash: "reshashUPDATED",
    is_success: false,
    status_code: 500,
    created_at: created.created_at,
  };

  const updated = await api.functional.apiOmnichannel.apiAudits.put(
    connection,
    created.id,
    updateBody
  );
  typia.assert<IApiAudit>(updated);
  TestValidator.equals("Update: response_hash")<string>(updateBody.response_hash)(updated.response_hash);
  TestValidator.equals("Update: is_success")<boolean>(updateBody.is_success)(updated.is_success);
  TestValidator.equals("Update: status_code")<number>(updateBody.status_code)(updated.status_code);
  TestValidator.equals("Update: id is persistent")<string>(created.id)(updated.id);
  TestValidator.equals("Update: created_at is preserved")<string>(created.created_at)(updated.created_at);

  // Step 3: Edge case: Try updating with a non-existent ID
  const nonExistentId = "c0a801ff-dead-beef-ffff-000000badbad";
  await TestValidator.httpError("Update with non-existent ID should fail")(404)(async () => {
    await api.functional.apiOmnichannel.apiAudits.put(
      connection,
      nonExistentId,
      updateBody
    );
  });

  // Step 4: Edge case: Malformed body (missing required field 'endpoint')
  const malformedBody = { ...updateBody };
  // @ts-expect-error
  delete malformedBody.endpoint;
  await TestValidator.httpError("Update with malformed body should fail")(400)(async () => {
    // @ts-expect-error
    await api.functional.apiOmnichannel.apiAudits.put(
      connection,
      created.id,
      malformedBody
    );
  });

  // Step 5: (Optional) Try extra/disallowed field
  const invalidBody = { ...updateBody, unexpected_prop: "not-allowed" };
  await TestValidator.httpError("Update with extra field should fail")(400)(async () => {
    // @ts-expect-error
    await api.functional.apiOmnichannel.apiAudits.put(
      connection,
      created.id,
      invalidBody
    );
  });

  // Step 6: Check persistence by fetching the audit entry again (assume GET not exposed, else validate via returned object)
  TestValidator.equals("Audit entry id remains the same after update")<string>(created.id)(updated.id);
}

