export async function test_soft_delete_api_audit_entry_and_inaccessible_afterwards(connection: api.IConnection): Promise<void> {
    // Step 1: Prepare and create a new API audit entry
    const input: IApiAudit.ICreate = {
        api_client_id: uuidv4(),
        api_token_id: uuidv4(),
        integration_channel_id: uuidv4(),
        endpoint: "/v1/payments/approve",
        http_method: "POST",
        actor_id: uuidv4(),
        decision_log_id: uuidv4(),
        request_hash: "request123abc",
        response_hash: "response456def",
        is_success: true,
        status_code: 200,
        created_at: new Date().toISOString(),
    };
    const created = await api.functional.apiOmnichannel.apiAudits.post(connection, input);
    typia.assert<IApiAudit>(created);
    TestValidator.equals("created.id should match type", () => false)(typeof created.id === "string" && created.id.length > 0);

    // Step 2: Soft-delete the entry
    const deleted = await api.functional.apiOmnichannel.apiAudits.delete(connection, created.id);
    typia.assert<IApiAudit>(deleted);
    TestValidator.equals("deleted.id matches created.id")<string>(created.id)(deleted.id);
    // Additional checks could include presence of deletion timestamp if the DTO was available

    // Step 3: Attempt to delete the already deleted entry
    await TestValidator.httpError("Deleting already deleted audit entry should throw error")(404)(() => api.functional.apiOmnichannel.apiAudits.delete(connection, created.id));

    // Step 4: Attempt to delete a non-existent entry
    const nonexist_id = uuidv4();
    await TestValidator.httpError("Deleting non-existent audit entry should throw error")(404)(() => api.functional.apiOmnichannel.apiAudits.delete(connection, nonexist_id));
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IIntegrationChannel";
import { IPageIIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIIntegrationChannel";

export async function test_list_and_search_integration_channels_with_filtering(connection: api.IConnection): Promise<void> {
    // 1. Create integration channels
    const inputs: IIntegrationChannel.ICreate[] = [
        { channel_code: "web", channel_name: "Web Portal" },
        { channel_code: "mobileapp", channel_name: "Mobile Application" },
        { channel_code: "partner_erp", channel_name: "Partner ERP" },
        { channel_code: "legacy", channel_name: "Legacy API Channel" },
    ];
    const created: IIntegrationChannel[] = [];
    for(const input of inputs) {
        const output = await api.functional.apiOmnichannel.integrationChannels.post(connection, input);
        typia.assert<IIntegrationChannel>(output);
        created.push(output);
    }

    // 2. List all channels, no filter
    const resp1 = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {});
    typia.assert<IPageIIntegrationChannel>(resp1);
    TestValidator.predicate("List includes all created")(resp1.data.length >= created.length);
    // 2a. Search by exact channel_code
    const filterByCode = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        filters: { channel_code: "web" },
    });
    typia.assert<IPageIIntegrationChannel>(filterByCode);
    TestValidator.equals("Search by channel_code count")([created.filter(x => x.channel_code === "web"]).length])(filterByCode.data.length);
    if(filterByCode.data.length) {
        TestValidator.equals("channel_code matches")("web")(filterByCode.data[0].channel_code);
    }

    // 2b. Search by partial channel_name (simulate 'like')
    const filterByName = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        filters: { channel_name: "Application" },
    });
    typia.assert<IPageIIntegrationChannel>(filterByName);
    TestValidator.predicate("Partial channel_name present")(filterByName.data.some(x => x.channel_name.includes("Application")));

    // 2c. Filter by is_active (should all be true)
    const filterByActive = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        filters: { is_active: true },
    });
    typia.assert<IPageIIntegrationChannel>(filterByActive);
    TestValidator.predicate("All are active")(filterByActive.data.every(x => x.is_active === true));

    // 2d. Pagination: limit=2, first page
    const page1 = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        pagination: { page: 1, limit: 2 },
    });
    typia.assert<IPageIIntegrationChannel>(page1);
    TestValidator.equals("Page1 limit")(2)(page1.data.length);
    TestValidator.equals("Page1 number")(1)(page1.pagination.current);

    // 2d. Pagination: limit=2, second page
    const page2 = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        pagination: { page: 2, limit: 2 },
    });
    typia.assert<IPageIIntegrationChannel>(page2);
    TestValidator.equals("Page2 limit")(2)(page2.data.length);
    TestValidator.equals("Page2 number")(2)(page2.pagination.current);

    // 2e. Out-of-range page
    const outOfRangePage = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        pagination: { page: 999, limit: 2 },
    });
    typia.assert<IPageIIntegrationChannel>(outOfRangePage);
    TestValidator.equals("Out of range is empty")([] as IIntegrationChannel[])(outOfRangePage.data);

    // 2f. Sorting by channel_name ascending and descending
    const sortedAsc = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        sort: { by: "channel_name", order: "asc" },
    });
    typia.assert<IPageIIntegrationChannel>(sortedAsc);
    TestValidator.predicate("Sorted ascending")(
        sortedAsc.data.every(
            (x, i, arr) => i === 0 || arr[i - 1].channel_name.localeCompare(x.channel_name) <= 0
        )
    );
    const sortedDesc = await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
        sort: { by: "channel_name", order: "desc" },
    });
    typia.assert<IPageIIntegrationChannel>(sortedDesc);
    TestValidator.predicate("Sorted descending")(
        sortedDesc.data.every(
            (x, i, arr) => i === 0 || arr[i - 1].channel_name.localeCompare(x.channel_name) >= 0
        )
    );

    // 3. Attempt invalid filter for is_active (inject string instead of boolean)
    await TestValidator.httpError("Invalid filter type") (400) (async () => {
        // @ts-expect-error: testing invalid filter deliberately
        await api.functional.apiOmnichannel.integrationChannels.patch(connection, {
            filters: { is_active: "NOT_A_BOOL" },
        });
    });
}

