import { v4 as uuidv4 } from "uuid";

export async function test_api_update_integration_channel_metadata_and_handle_edge_cases(connection: api.IConnection): Promise<void> {
    // 1. Create a new integration channel
    const createInput: IIntegrationChannel.ICreate = {
        channel_code: `channel_${Date.now()}`,
        channel_name: `Test Channel ${Date.now()}`,
    };
    const created = await api.functional.apiOmnichannel.integrationChannels.post(connection, createInput);

    typia.assert<IIntegrationChannel>(created);
    TestValidator.equals("channel_code should match input")<string>(createInput.channel_code)(created.channel_code);
    TestValidator.equals("channel_name should match input")<string>(createInput.channel_name)(created.channel_name);

    // 2. Update the created channel: change name and is_active
    const updateInput: IIntegrationChannel.IUpdate = {
        channel_name: createInput.channel_name + " - Updated",
        is_active: false
    };
    const updated = await api.functional.apiOmnichannel.integrationChannels.put(connection, created.id, updateInput);
    typia.assert<IIntegrationChannel>(updated);
    TestValidator.equals("channel_name updated")<string>(updateInput.channel_name!)(updated.channel_name);
    TestValidator.equals("is_active updated")<boolean>(updateInput.is_active!)(updated.is_active);
    // Ensure immutable fields not changed
    TestValidator.equals("id should remain unchanged")<string>(created.id)(updated.id);
    TestValidator.equals("channel_code should remain unchanged")<string>(created.channel_code)(updated.channel_code);
    TestValidator.equals("created_at should remain unchanged")<string>(created.created_at)(updated.created_at);

    // 3. Attempt to update with a non-existent (random) UUID
    const randomUUID = uuidv4();
    await TestValidator.httpError("update with non-existent id should return error")(404, 400)(async () => {
        return api.functional.apiOmnichannel.integrationChannels.put(connection, randomUUID, updateInput);
    });

    // 4. Attempt to update with a malformed (invalid) UUID
    const badUUID = "123-not-a-uuid";
    await TestValidator.httpError("update with invalid id should return error")(400)(async () => {
        return api.functional.apiOmnichannel.integrationChannels.put(connection, badUUID, updateInput);
    });

    // 5. Attempt to update with valid id and malformed payload (missing fields, wrong types)
    // malformed: wrong type for is_active
    const malformedInput = { channel_name: 42, is_active: "yes" } as any;
    await TestValidator.httpError("update with invalid payload should fail")(400)(async () => {
        return api.functional.apiOmnichannel.integrationChannels.put(connection, created.id, malformedInput);
    });
    // malformed: empty object
    await TestValidator.httpError("update with empty payload should fail")(400)(async () => {
        return api.functional.apiOmnichannel.integrationChannels.put(connection, created.id, {} as any);
    });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IIntegrationChannel } from "@ORGANIZATION/PROJECT-api/lib/structures/IIntegrationChannel";

export async function test_api_soft_delete_integration_channel_and_validate_compliance(connection: api.IConnection): Promise<void> {
    // Step 1: Create a new integration channel
    const createInput: IIntegrationChannel.ICreate = {
        channel_code: `test-code-${Date.now()}`,
        channel_name: `Test Channel ${Date.now()}`
    };
    const created = await api.functional.apiOmnichannel.integrationChannels.post(connection, createInput);
    typia.assert<IIntegrationChannel>(created);

    // Step 2: Soft-delete the integration channel
    const deleted = await api.functional.apiOmnichannel.integrationChannels.delete(connection, created.id);
    typia.assert<IIntegrationChannel.IDelete>(deleted);
    TestValidator.equals("deleted channel id")(created.id)(deleted.id);
    TestValidator.predicate("deleted_at is a non-empty string")(() => typeof deleted.deleted_at === "string" && deleted.deleted_at.length > 0);

    // Step 3: Attempt to soft-delete the same channel again - expect error
    await TestValidator.error("deleting already deleted channel should fail")(async () => {
        await api.functional.apiOmnichannel.integrationChannels.delete(connection, created.id);
    });

    // Step 4: Attempt to soft-delete an invalid / non-existent ID
    const invalidId = "deadbeef-dead-dead-dead-beefdeadbeef"; // fake UUID
    await TestValidator.error("deleting a non-existent channel should fail")(async () => {
        await api.functional.apiOmnichannel.integrationChannels.delete(connection, invalidId);
    });
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IChannelSession";
import { IPageIChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIChannelSession";

export async function test_list_and_search_channel_sessions_with_pagination(connection: api.IConnection): Promise<void> {
  // 1. Seed several channel sessions with differing fields
  const now = new Date();
  const makeId = () => crypto.randomUUID();
  const integration_channel_id1 = makeId();
  const integration_channel_id2 = makeId();
  const api_token_id1 = makeId();
  const api_token_id2 = makeId();
  const user_id1 = makeId();
  const user_id2 = makeId();
  const session_key1 = `sesskey-${makeId()}`;
  const session_key2 = `sesskey-${makeId()}`;
  const seedPayloads: IChannelSession.ICreate[] = [
    {
      integration_channel_id: integration_channel_id1,
      api_token_id: api_token_id1,
      user_id: user_id1,
      session_key: session_key1,
      started_at: now.toISOString(),
    },
    {
      integration_channel_id: integration_channel_id2,
      api_token_id: api_token_id2,
      user_id: user_id2,
      session_key: session_key2,
      started_at: now.toISOString(),
    },
    {
      integration_channel_id: integration_channel_id1,
      api_token_id: api_token_id2,
      user_id: null,
      session_key: `anonymous-${makeId()}`,
      started_at: now.toISOString(),
    }
  ];
  const created: IChannelSession[] = [];
  for (const payload of seedPayloads) {
    const session = await api.functional.apiOmnichannel.channelSessions.post(connection, payload);
    typia.assert<IChannelSession>(session);
    created.push(session);
  }

  // 2. Basic List: no filter
  const reqAll = { };
  const listAll = await api.functional.apiOmnichannel.channelSessions.patch(connection, reqAll);
  typia.assert<IPageIChannelSession>(listAll);
  TestValidator.predicate("Should list seeded sessions")(() => listAll.data.some(d => created.some(s => s.id === d.id)));

  // 3. Filter: by integration_channel_id
  const filterByChannel: IChannelSession.IRequest = {
    filters: { integration_channel_id: integration_channel_id1 },
    pagination: { page: 1, limit: 10 },
  };
  const filtered = await api.functional.apiOmnichannel.channelSessions.patch(connection, filterByChannel);
  typia.assert<IPageIChannelSession>(filtered);
  TestValidator.equals("Filter should only return relevant channel id")(
    true
  )(
    filtered.data.every(d => d.integration_channel_id === integration_channel_id1)
  );
  TestValidator.predicate("Some filtered records should match seeded")(() => filtered.data.some(d => created.some(s => s.id === d.id)));

  // 4. Pagination: use small limit, get 1st and 2nd page
  const pagReq: IChannelSession.IRequest = { pagination: { page: 1, limit: 1 } };
  const page1 = await api.functional.apiOmnichannel.channelSessions.patch(connection, pagReq);
  typia.assert<IPageIChannelSession>(page1);
  TestValidator.equals("Pagination: limit")(
    1
  )(page1.data.length);
  const pagReq2: IChannelSession.IRequest = { pagination: { page: 2, limit: 1 } };
  const page2 = await api.functional.apiOmnichannel.channelSessions.patch(connection, pagReq2);
  typia.assert<IPageIChannelSession>(page2);
  TestValidator.equals("Pagination: limit on 2nd page")(
    1
  )(page2.data.length);
  TestValidator.predicate("Page change should alter results")(
    () => page1.data.length === 1 && page2.data.length === 1 && page1.data[0].id !== page2.data[0].id
  );

  // 5. Out-of-bounds page (too high)
  const outReq: IChannelSession.IRequest = { pagination: { page: 1000, limit: 10 } };
  const outPage = await api.functional.apiOmnichannel.channelSessions.patch(connection, outReq);
  typia.assert<IPageIChannelSession>(outPage);
  TestValidator.equals("Too high page returns empty array")([] as IChannelSession[])(outPage.data);

  // 6. Invalid Input: negative/zero page
  await TestValidator.httpError("Zero page should error")(400)(
    () => api.functional.apiOmnichannel.channelSessions.patch(connection, { pagination: { page: 0, limit: 10 } })
  );
  await TestValidator.httpError("Negative page should error")(400)(
    () => api.functional.apiOmnichannel.channelSessions.patch(connection, { pagination: { page: -1, limit: 10 } })
  );
  await TestValidator.httpError("Negative limit should error")(400)(
    () => api.functional.apiOmnichannel.channelSessions.patch(connection, { pagination: { page: 1, limit: -5 } })
  );

  // 7. Invalid filter type (malformed UUID)
  await TestValidator.httpError("Malformed UUID")(400)(
    () => api.functional.apiOmnichannel.channelSessions.patch(connection, { filters: { integration_channel_id: "not-a-uuid" } })
  );

  // 8. Non-existent filter value
  const noneReq: IChannelSession.IRequest = { filters: { session_key: `never-${makeId()}` } };
  const none = await api.functional.apiOmnichannel.channelSessions.patch(connection, noneReq);
  typia.assert<IPageIChannelSession>(none);
  TestValidator.equals("Non-existent filter yields empty array")([] as IChannelSession[])(none.data);
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IChannelSession";
import typia from "typia";

export async function test_api_get_channel_session_by_id_and_validate_errors(connection: api.IConnection): Promise<void> {
  // 1. Prepare test data for channel session creation
  const sessionInput: IChannelSession.ICreate = {
    integration_channel_id: "11111111-1111-1111-1111-111111111111",
    api_token_id: "22222222-2222-2222-2222-222222222222",
    user_id: "33333333-3333-3333-3333-333333333333",
    session_key: `session-key-${Date.now()}`,
    started_at: new Date().toISOString(),
  };

  // 2. Create a channel session
  const createdSession = await api.functional.apiOmnichannel.channelSessions.post(
    connection,
    { body: sessionInput }
  );
  typia.assert<IChannelSession>(createdSession);

  // Ensure returned fields match at least the submitted values
  TestValidator.equals("integration_channel_id")(sessionInput.integration_channel_id)(createdSession.integration_channel_id);
  TestValidator.equals("api_token_id")(sessionInput.api_token_id)(createdSession.api_token_id);
  TestValidator.equals("user_id")(sessionInput.user_id)(createdSession.user_id);
  TestValidator.equals("session_key")(sessionInput.session_key)(createdSession.session_key);
  TestValidator.equals("started_at")(sessionInput.started_at)(createdSession.started_at);

  // 3. Retrieve details for the created channel session by ID
  const gottenSession = await api.functional.apiOmnichannel.channelSessions.get(
    connection,
    createdSession.id
  );
  typia.assert<IChannelSession>(gottenSession);
  TestValidator.equals("id")(createdSession.id)(gottenSession.id);
  TestValidator.equals("integration_channel_id")(createdSession.integration_channel_id)(gottenSession.integration_channel_id);
  TestValidator.equals("api_token_id")(createdSession.api_token_id)(gottenSession.api_token_id);
  TestValidator.equals("user_id")(createdSession.user_id)(gottenSession.user_id);
  TestValidator.equals("session_key")(createdSession.session_key)(gottenSession.session_key);
  TestValidator.equals("started_at")(createdSession.started_at)(gottenSession.started_at);
  TestValidator.equals("ended_at")(createdSession.ended_at)(gottenSession.ended_at);

  // 4. Negative case: try to retrieve a non-existent session by ID
  const invalidId = "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa";
  await TestValidator.httpError("Expect 404 or 400 for unknown id")(404, 400)(
    async () => {
      await api.functional.apiOmnichannel.channelSessions.get(
        connection,
        invalidId
      );
    }
  );
}

