import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IChannelSession } from "@ORGANIZATION/PROJECT-api/lib/structures/IChannelSession";

export async function test_update_channel_session_metadata_and_handle_errors(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a channel session
  const createInput: IChannelSession.ICreate = {
    integration_channel_id: "c1c173d2-36e4-49b7-887a-7ec12abe40f5",
    api_token_id: "e0e61635-646f-4cca-9089-40c40c09af64",
    user_id: null, // can also test with a string
    session_key: `sess_${Math.random().toString(36).slice(2)}`,
    started_at: new Date().toISOString(),
  };

  const created = await api.functional.apiOmnichannel.channelSessions.post(
    connection,
    createInput,
  );
  typia.assert<IChannelSession>(created);
  // Validate top fields
  TestValidator.equals("created.integration_channel_id")(
    createInput.integration_channel_id
  )(created.integration_channel_id);
  TestValidator.equals("created.api_token_id")(createInput.api_token_id)(
    created.api_token_id
  );
  TestValidator.equals("created.session_key")(createInput.session_key)(
    created.session_key
  );
  TestValidator.equals("created.user_id")(createInput.user_id)(created.user_id);
  TestValidator.equals("created.started_at")(createInput.started_at)(
    created.started_at
  );
  TestValidator.equals("created.ended_at")<null>(null)(created.ended_at);

  // 2. Update the session: set ended_at, and user_id (simulate login handoff)
  const updateInput: IChannelSession.IUpdate = {
    user_id: "deada106-650b-489c-90a9-cf5bc839b423",
    ended_at: new Date(Date.now() + 1000 * 60).toISOString(),
  };
  const updated = await api.functional.apiOmnichannel.channelSessions.put(
    connection,
    created.id,
    updateInput,
  );
  typia.assert<IChannelSession>(updated);
  TestValidator.equals("updated.id")(created.id)(updated.id);
  TestValidator.equals("updated.integration_channel_id")(created.integration_channel_id)(
    updated.integration_channel_id
  );
  TestValidator.equals("updated.api_token_id")(created.api_token_id)(
    updated.api_token_id
  );
  TestValidator.equals("updated.session_key")(created.session_key)(
    updated.session_key
  );
  TestValidator.equals("updated.user_id")(updateInput.user_id)(
    updated.user_id
  );
  TestValidator.equals("updated.ended_at")(updateInput.ended_at)(
    updated.ended_at
  );
  // started_at should remain the same
  TestValidator.equals("updated.started_at")(created.started_at)(
    updated.started_at
  );

  // 3. Attempt updating nonexistent session (should be 404 error or similar)
  await TestValidator.httpError("update non-existent session")(
    404,
    400,
  )(() =>
    api.functional.apiOmnichannel.channelSessions.put(
      connection,
      "00000000-0000-0000-0000-000000000000",
      updateInput,
    ),
  );

  // 4. Attempt update with invalid payload: no updatable props (should error)
  const invalidUpdate: Partial<IChannelSession.IUpdate> = {};
  await TestValidator.httpError("update with empty payload")(400)(() =>
    api.functional.apiOmnichannel.channelSessions.put(
      connection,
      created.id,
      invalidUpdate as IChannelSession.IUpdate,
    ),
  );
}

