import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IWebhookEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IWebhookEvent";
import { IPageIWebhookEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIWebhookEvent";
import typia from "typia";

export async function test_api_webhook_event_list_and_search_webhook_events_with_varied_criteria(connection: api.IConnection): Promise<void> {
    // 1. Create two events with different event_types and delivery_status (simulate via creation)
    const eventAInput: IWebhookEvent.ICreate = {
        integration_channel_id: "11111111-1111-1111-1111-111111111111",
        event_type: "order.updated",
        destination_url: "https://webhook-handler.example.com/notify-order",
        event_hash: "evthash1"
    };
    const eventBInput: IWebhookEvent.ICreate = {
        integration_channel_id: "22222222-2222-2222-2222-222222222222",
        event_type: "payment.completed",
        destination_url: "https://webhook-handler.example.com/notify-payment",
        event_hash: "evthash2"
    };

    const createdA = await api.functional.apiOmnichannel.webhookEvents.post(connection, eventAInput);
    const createdB = await api.functional.apiOmnichannel.webhookEvents.post(connection, eventBInput);

    typia.assert<IWebhookEvent>(createdA);
    typia.assert<IWebhookEvent>(createdB);

    // For test, set filter values:
    const now = new Date();
    const isoNow = now.toISOString();
    const tenMinutesAgo = new Date(now.getTime() - 10 * 60 * 1000).toISOString();
    const inTenMinutes = new Date(now.getTime() + 10 * 60 * 1000).toISOString();

    // 2. Positive: List all (no filter)
    const reqAll: IWebhookEvent.IRequest = {};
    const pageAll = await api.functional.apiOmnichannel.webhookEvents.patch(connection, reqAll);
    typia.assert<IPageIWebhookEvent>(pageAll);
    TestValidator.predicate("Contains created events")( () => {
        return pageAll.data.some(e => e.id === createdA.id) && pageAll.data.some(e => e.id === createdB.id);
    });

    // 2. Filter by event_type: only eventA
    const reqAType: IWebhookEvent.IRequest = { event_type: eventAInput.event_type };
    const pageAType = await api.functional.apiOmnichannel.webhookEvents.patch(connection, reqAType);
    typia.assert<IPageIWebhookEvent>(pageAType);
    TestValidator.predicate("Only eventA type present")( () => {
        return pageAType.data.every(e => e.event_type === eventAInput.event_type);
    });
    TestValidator.predicate("Contains eventA")( () => pageAType.data.some(e => e.id === createdA.id));

    // 2. Filter by event_type: only eventB
    const reqBType: IWebhookEvent.IRequest = { event_type: eventBInput.event_type };
    const pageBType = await api.functional.apiOmnichannel.webhookEvents.patch(connection, reqBType);
    typia.assert<IPageIWebhookEvent>(pageBType);
    TestValidator.predicate("Only eventB type present")( () => {
        return pageBType.data.every(e => e.event_type === eventBInput.event_type);
    });
    TestValidator.predicate("Contains eventB")( () => pageBType.data.some(e => e.id === createdB.id));

    // 2. Filter by delivery_status (simulate 'pending' as initial status)
    const reqPending: IWebhookEvent.IRequest = { delivery_status: "pending" };
    const pagePending = await api.functional.apiOmnichannel.webhookEvents.patch(connection, reqPending);
    typia.assert<IPageIWebhookEvent>(pagePending);
    TestValidator.predicate("All delivery_status pending")( () => {
        return pagePending.data.every(e => e.delivery_status === "pending");
    });

    // 2. Filter by created_from/created_to (should contain both in time window)
    const reqWindow: IWebhookEvent.IRequest = { created_from: tenMinutesAgo, created_to: inTenMinutes };
    const pageWindow = await api.functional.apiOmnichannel.webhookEvents.patch(connection, reqWindow);
    typia.assert<IPageIWebhookEvent>(pageWindow);
    TestValidator.predicate("All created_at in window")( () =>
        pageWindow.data.every(e => new Date(e.created_at) >= new Date(tenMinutesAgo) && new Date(e.created_at) <= new Date(inTenMinutes))
    );

    // 3. Negative: invalid delivery_status
    const reqInvalidStatus: IWebhookEvent.IRequest = { delivery_status: "inexistent" as any };
    await TestValidator.httpError("Invalid delivery_status")(400)(async () => {
