import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAIFeatureStore } from "@ORGANIZATION/PROJECT-api/lib/structures/IAIFeatureStore";
import { IPageIAIFeatureStore } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAIFeatureStore";
import typia from "typia";

export async function test_api_list_ai_feature_store_with_filtering_and_pagination(connection: api.IConnection): Promise<void> {
    // Wide search (no filters)
    const wideRequest = {};
    const wideResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, wideRequest);
    typia.assert<IPageIAIFeatureStore>(wideResult);
    TestValidator.predicate("Wide search returns data")(() => wideResult.data.length >= 0);
    const allIds = wideResult.data.map(e => e.id);

    // If there are no records, skip further filter tests
    if (wideResult.data.length === 0) return;

    const sample = wideResult.data[0];

    // Narrow filter on model version
    const filterByModel = { ai_model_version_id: sample.ai_model_version_id };
    const narrowModelResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, filterByModel);
    typia.assert<IPageIAIFeatureStore>(narrowModelResult);
    TestValidator.predicate("Model version filter")(() => narrowModelResult.data.every(e => e.ai_model_version_id === sample.ai_model_version_id));

    // Narrow filter on name (should be at least one result if that name exists)
    const filterByName = { name: sample.name };
    const narrowNameResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, filterByName);
    typia.assert<IPageIAIFeatureStore>(narrowNameResult);
    TestValidator.predicate("Name filter")(() => narrowNameResult.data.every(e => e.name === sample.name));

    // Pagination: get first page with limit 1
    const pagedRequest = { page: 1, limit: 1 };
    const pagedResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, pagedRequest);
    typia.assert<IPageIAIFeatureStore>(pagedResult);
    TestValidator.equals("Page number")<number>(1)(pagedResult.pagination.current);
    TestValidator.equals("Page limit")<number>(1)(pagedResult.pagination.limit);
    TestValidator.predicate("Only one record on first page")(() => pagedResult.data.length === 1 || pagedResult.data.length === 0);

    // Out-of-range pagination: high page number
    const outOfRangeRequest = { page: 99999, limit: 1 };
    const outOfRangeResult = await api.functional.advancedAI.aiFeatureStore.patch(connection, outOfRangeRequest);
    typia.assert<IPageIAIFeatureStore>(outOfRangeResult);
    TestValidator.equals("Empty data for out-of-range page")<IAIFeatureStore[]>([])(outOfRangeResult.data);
}

