import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAIFeatureStore } from "@ORGANIZATION/PROJECT-api/lib/structures/IAIFeatureStore";
import typia from "typia";

export async function test_update_ai_feature_store_record_with_various_conditions(connection: api.IConnection): Promise<void> {
    // 1. Create a feature store record
    const createInput: IAIFeatureStore.ICreate = {
        ai_model_version_id: "00000000-0000-0000-0000-000000000001",
        name: "initial_feature_set",
        schema: "{\"fields\":[{\"name\":\"input\",\"type\":\"float\"}]}",
        description: "Initial version for update test",
        ai_training_data_id: "00000000-0000-0000-0000-000000000111",
    };
    const created = await api.functional.advancedAI.aiFeatureStore.post(connection, createInput);
    typia.assert<IAIFeatureStore>(created);
    TestValidator.predicate("feature store created")(!!created.id && !!created.ai_model_version_id);

    // 2. Prepare update payload (happy path)
    const updateInput: IAIFeatureStore.IUpdate = {
        name: "updated_feature_set",
        description: "Updated for compliance",
        schema: "{\"fields\":[{\"name\":\"output\",\"type\":\"double\"}]}",
        ai_training_data_id: "00000000-0000-0000-0000-000000000112",
    };
    const updated = await api.functional.advancedAI.aiFeatureStore.put(connection, created.id, updateInput);
    typia.assert<IAIFeatureStore>(updated);
    TestValidator.equals("id unchanged")(created.id)(updated.id);
    TestValidator.equals("model version unchanged")(created.ai_model_version_id)(updated.ai_model_version_id);
    TestValidator.equals("updated name")(<string>updateInput.name)(updated.name);
    TestValidator.equals("updated desc")(<string>updateInput.description)(updated.description);
    TestValidator.equals("updated schema")(<string>updateInput.schema)(updated.schema);
    TestValidator.equals("updated training data")(<string>updateInput.ai_training_data_id)(updated.ai_training_data_id);
    TestValidator.equals("created_at unchanged")(created.created_at)(updated.created_at);

    // 3. Invalid ID (malformed UUID)
    await TestValidator.httpError("update with malformed UUID") (400)(
        () => api.functional.advancedAI.aiFeatureStore.put(connection, "not-a-uuid", updateInput),
    );
    // 4. Invalid ID (random UUID)
    await TestValidator.httpError("update with random unknown UUID") (404)(
        () => api.functional.advancedAI.aiFeatureStore.put(connection, "123e4567-e89b-12d3-a456-426614174999", updateInput),
    );

    // 5. Invalid data (empty schema)
    const invalidInput: IAIFeatureStore.IUpdate = {
        schema: ""
    };
    await TestValidator.httpError("update with invalid empty schema") (400)(
        () => api.functional.advancedAI.aiFeatureStore.put(connection, created.id, invalidInput),
    );

    // 6. Update with missing fields (all optional, so expect success with no change)
    const noOpUpdate: IAIFeatureStore.IUpdate = {};
    const noOpUpdated = await api.functional.advancedAI.aiFeatureStore.put(connection, created.id, noOpUpdate);
    typia.assert<IAIFeatureStore>(noOpUpdated);
    TestValidator.equals("no changes when update is empty")(updated)(noOpUpdated);

    // 7. (Optional) Try conflict: Create another record, try duplicate name for same model version
    const createInput2: IAIFeatureStore.ICreate = {
        ai_model_version_id: createInput.ai_model_version_id,
        name: "another_feature_set",
        schema: "{\"fields\":[{\"name\":\"input2\",\"type\":\"int\"}]}",
        description: "Another set for name conflict",
        ai_training_data_id: "00000000-0000-0000-0000-000000000113",
    };
    const created2 = await api.functional.advancedAI.aiFeatureStore.post(connection, createInput2);
    typia.assert<IAIFeatureStore>(created2);
    // try to update created2 to an existing name (should conflict)
    await TestValidator.httpError("duplicate name conflict") (409)(
        () => api.functional.advancedAI.aiFeatureStore.put(connection, created2.id, { name: updateInput.name }),
    );
}

