import { IPageIAIDecisionLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAIDecisionLog";
import typia from "typia";

export async function test_api_list_ai_decision_logs_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Fetch with no filters - should get some data (if any exist)
  const req1 = {};
  const res1 = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection,
    req1,
  );
  typia.assert<IPageIAIDecisionLog>(res1);
  TestValidator.equals("pagination.records matches data length or is 0", () => true)(
    res1.pagination.records >= res1.data.length,
  );

  // 2. Fetch first page, limit=2, ordered by created_at desc
  const req2 = { page: 1, limit: 2, order_by: "created_at", order: "desc" };
  const res2 = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection,
    req2,
  );
  typia.assert<IPageIAIDecisionLog>(res2);
  TestValidator.equals("data <= limit", () => true)(
    res2.data.length <= 2,
  );

  // 3. If there's at least one log, do a filtered fetch by ai_model_version_id
  if (res1.data.length > 0) {
    const logSample = res1.data[0];
    const req3 = {
      ai_model_version_id: logSample.ai_model_version_id,
      decision: logSample.decision,
      page: 1,
      limit: 5,
    };
    const res3 = await api.functional.advancedAI.aiDecisionLogs.patch(
      connection,
      req3,
    );
    typia.assert<IPageIAIDecisionLog>(res3);
    // All returned logs should have matching ai_model_version_id and decision
    for (const log of res3.data) {
      TestValidator.equals("ai_model_version_id", () => true)(
        log.ai_model_version_id === logSample.ai_model_version_id,
      );
      TestValidator.equals("decision", () => true)(
        log.decision === logSample.decision,
      );
    }
  }

  // 4. Empty/negative filter: UUID that should not match
  const req4 = {
    ai_model_version_id:
      "00000000-0000-0000-0000-000000000000", // unlikely UUID
    page: 1,
    limit: 3,
  };
  const res4 = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection,
    req4,
  );
  typia.assert<IPageIAIDecisionLog>(res4);
  TestValidator.equals("empty result", () => true)(
    res4.data.length === 0,
  );

  // 5. Out-of-bounds page
  const req5 = { page: 999999, limit: 2 };
  const res5 = await api.functional.advancedAI.aiDecisionLogs.patch(
    connection,
    req5,
  );
  typia.assert<IPageIAIDecisionLog>(res5);
  TestValidator.equals("empty on out-of-bounds page", () => true)(
    res5.data.length === 0,
  );

  // 6. Invalid filter value (bad date)
  const req6 = { created_at__gte: "not-a-date" };
  await TestValidator.httpError("invalid date input")("422")(
    () => api.functional.advancedAI.aiDecisionLogs.patch(connection, req6),
  );
}

