// Utility to generate a valid random UUID (no dependency)
function generateRandomUUID(): string {
  // Not cryptographically strong, but suitable for E2E deterministic test
  return "xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx".replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0, v = c === "x" ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

export async function test_api_get_ai_decision_log_by_id_and_handle_not_found(connection: api.IConnection): Promise<void> {
  // 1. Create a new decision log
  const createInput: IAIDecisionLog.ICreate = {
    ai_model_version_id: generateRandomUUID(),
    target_user_id: null,
    target_entity: "order",
