    decision: "approve",
    input_snapshot: '{"orderTotal": 120, "currency": "USD"}',
    rationale: "Order passed all risk rules"
  };

  const created: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.post(connection, createInput);
  typia.assert<IAIDecisionLog>(created);

  // 2. Retrieve by ID
  const retrieved: IAIDecisionLog = await api.functional.advancedAI.aiDecisionLogs.get(connection, created.id);
  typia.assert<IAIDecisionLog>(retrieved);

  // 3. Validate record integrity (excluding generated id and created_at)
  TestValidator.equals("ai_model_version_id")(createInput.ai_model_version_id)(retrieved.ai_model_version_id);
  TestValidator.equals("target_user_id")(createInput.target_user_id)(retrieved.target_user_id);
  TestValidator.equals("target_entity")(createInput.target_entity)(retrieved.target_entity);
  TestValidator.equals("target_entity_id")(createInput.target_entity_id)(retrieved.target_entity_id);
  TestValidator.equals("decision")(createInput.decision)(retrieved.decision);
  TestValidator.equals("input_snapshot")(createInput.input_snapshot)(retrieved.input_snapshot);
  TestValidator.equals("rationale")(createInput.rationale)(retrieved.rationale);

  // Validate that "id" and "created_at" fields are present
  TestValidator.predicate("id should be a non-empty string")(() => typeof retrieved.id === "string" && !!retrieved.id);
  TestValidator.predicate("created_at should be a non-empty string")(() => typeof retrieved.created_at === "string" && !!retrieved.created_at);

  // 4. Try to fetch with a non-existent UUID
  const invalidId = generateRandomUUID();
  await TestValidator.httpError("GET non-existent AI decision log should return 404")(404)(() =>
    api.functional.advancedAI.aiDecisionLogs.get(connection, invalidId)
