import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAIDecisionLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IAIDecisionLog";
import typia from "typia";

export async function test_api_update_ai_decision_log_by_id_with_various_conditions(connection: api.IConnection): Promise<void> {
    // 1. Create a valid AI decision log entry
    const createInput: IAIDecisionLog.ICreate = {
        ai_model_version_id: "aaaa1111-1111-1111-1111-111111111111",
        target_entity: "order",
        target_entity_id: "bbbb2222-2222-2222-2222-222222222222",
        decision: "approve",
        input_snapshot: "{\"feature\":42}",
        target_user_id: "cccc3333-3333-3333-3333-333333333333",
        rationale: "Initial decision rationale"
    };
    const created = await api.functional.advancedAI.aiDecisionLogs.post(connection, createInput);
    typia.assert<IAIDecisionLog>(created);
    TestValidator.equals("after creation")<string>(() => createInput.rationale ?? null)(() => created.rationale ?? null);

    // 2. Update rationale (happy path)
    const updateInput: IAIDecisionLog.IUpdate = {
        rationale: "Updated decision rationale"
    };
    const updated = await api.functional.advancedAI.aiDecisionLogs.put(connection, created.id, updateInput);
    typia.assert<IAIDecisionLog>(updated);
    // Rationale should have changed
    TestValidator.equals("rationale updated")(updateInput.rationale)(updated.rationale);
    // Immutable fields must not change
    TestValidator.equals("id unchanged")(created.id)(updated.id);
    TestValidator.equals("ai_model_version_id unchanged")(created.ai_model_version_id)(updated.ai_model_version_id);
    TestValidator.equals("target_entity unchanged")(created.target_entity)(updated.target_entity);
    TestValidator.equals("target_entity_id unchanged")(created.target_entity_id)(updated.target_entity_id);
    TestValidator.equals("decision unchanged")(created.decision)(updated.decision);
    TestValidator.equals("input_snapshot unchanged")(created.input_snapshot)(updated.input_snapshot);
    TestValidator.equals("created_at unchanged")(created.created_at)(updated.created_at);
    // target_user_id may be null or a string
    TestValidator.equals("target_user_id unchanged")(created.target_user_id)(updated.target_user_id);

    // 3. Attempt update with invalid (non-existent) log ID
    await TestValidator.httpError("Invalid ID should error")(404)(
        () => api.functional.advancedAI.aiDecisionLogs.put(connection, "deadbeef-dead-dead-dead-deadbeefdead", updateInput)
    );

    // 4. Attempt update with invalid payload (unreasonably long rationale sample)
    const tooLongRationale = "x".repeat(10000);
    const invalidInput: IAIDecisionLog.IUpdate = { rationale: tooLongRationale };
    await TestValidator.error("Invalid payload should error")(
        () => api.functional.advancedAI.aiDecisionLogs.put(connection, created.id, invalidInput)
    );

    // 5. Attempt to update immutable fields (should ignore; use type-unsafe cast to attempt mutation)
    // Our DTO does not allow, so we directly cast.
    const illegalUpdate = { decision: "reject" } as any;
    await TestValidator.error("Immutable fields should not be updateable")(
        () => api.functional.advancedAI.aiDecisionLogs.put(connection, created.id, illegalUpdate)
    );
}

