import typia from "typia";

export async function test_api_list_ai_feedback_records_with_filters(connection: api.IConnection): Promise<void> {
  // 1. List all (no filters, default page/limit)
  {
    const body: Partial<IAiFeedback.IRequest> = {};
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    TestValidator.predicate("no filter: data is array")(() => Array.isArray(page.data));
    TestValidator.predicate("no filter: pagination meta")(
      () => typeof page.pagination.current === "number" && typeof page.pagination.limit === "number"
    );
  }

  // 2. Try filtering by user_id -- (replace with an actual UUID value present in data or use a likely non-hit)
  const testUserId = "00000000-0000-0000-0000-000000000001";
  {
    const body: Partial<IAiFeedback.IRequest> = { user_id: testUserId };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    if (page.data.length) {
      for (const f of page.data) {
        TestValidator.equals("user_id matches")(testUserId)(f.user_id);
      }
    }
  }

  // 3. Filter by feedback_type (simulate some sensible and some rare value)
  const testFeedbackType = "positive";
  {
    const body: Partial<IAiFeedback.IRequest> = { feedback_type: testFeedbackType };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    if (page.data.length) {
      for (const f of page.data) {
        TestValidator.equals("feedback_type matches")(testFeedbackType)(f.feedback_type);
      }
    }
  }

  // 4. Combined filter: user_id + feedback_type
  {
    const body: Partial<IAiFeedback.IRequest> = { user_id: testUserId, feedback_type: testFeedbackType };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    if (page.data.length) {
      for (const f of page.data) {
        TestValidator.equals("user_id matches")(testUserId)(f.user_id);
        TestValidator.equals("feedback_type matches")(testFeedbackType)(f.feedback_type);
      }
    }
  }

  // 5. Date range: only include feedback since a given time
  const timeStart = "2024-01-01T00:00:00.000Z";
  const timeEnd = "2026-01-01T00:00:00.000Z";
  {
    const body: Partial<IAiFeedback.IRequest> = { time_range_start: timeStart, time_range_end: timeEnd };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    if (page.data.length) {
      for (const f of page.data) {
        TestValidator.predicate("within time range")(
          () => f.created_at >= timeStart && f.created_at <= timeEnd
        );
      }
    }
  }

  // 6. Pagination: limit/page
  {
    const body: Partial<IAiFeedback.IRequest> = { page: 2, limit: 2 };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    TestValidator.equals("pagination limit matches")(2)(page.pagination.limit);
    TestValidator.equals("pagination page matches")(2)(page.pagination.current);
  }

  // 7. Sorting asc/desc
  for (const sort_order of ["asc", "desc"] as const) {
    const body: Partial<IAiFeedback.IRequest> = { sort_order, limit: 3 };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    if (page.data.length >= 2) {
      for (let i = 1; i < page.data.length; ++i) {
        if (sort_order === "asc") {
          TestValidator.predicate("ascending order")(
            () => page.data[i - 1].created_at <= page.data[i].created_at
          );
        } else {
          TestValidator.predicate("descending order")(
            () => page.data[i - 1].created_at >= page.data[i].created_at
          );
        }
      }
    }
  }

  // 8. Negative: excessive limit
  {
    const body: Partial<IAiFeedback.IRequest> = { limit: 5000 };
    await TestValidator.httpError("excessive limit triggers error")(400)(() =>
      api.functional.advancedAI.aiFeedback.patch(connection, { body })
    );
  }

  // 9. Negative: invalid UUID
  {
    const body: Partial<IAiFeedback.IRequest> = { user_id: "not-a-uuid" };
    await TestValidator.httpError("invalid UUID triggers error")(400)(() =>
      api.functional.advancedAI.aiFeedback.patch(connection, { body })
    );
  }

  // 10. Negative: nonsensical date range (start > end)
  {
    const body: Partial<IAiFeedback.IRequest> = { time_range_start: "2026-01-01T00:00:00.000Z", time_range_end: "2024-01-01T00:00:00.000Z" };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    TestValidator.equals("nonsensical date yields empty array")([] as IAiFeedback[])(page.data);
  }

  // 11. Edge: filter that yields no results
  {
    const body: Partial<IAiFeedback.IRequest> = { feedback_type: "__nonexistent_type_" };
    const page = await api.functional.advancedAI.aiFeedback.patch(connection, { body });
    typia.assert<IPageIAiFeedback>(page);
    TestValidator.equals("empty result on unmatched feedback_type")([] as IAiFeedback[])(page.data);
  }
}

