import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IAiFeedback } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiFeedback";

export async function test_api_create_ai_feedback_record_with_valid_invalid_and_duplicate_data(
    connection: api.IConnection,
): Promise<void> {
    // 1. Prepare valid input
    const validInput: IAiFeedback.ICreate = {
        decision_log_id: "00000000-0000-0000-0000-000000000001",
        user_id: "00000000-0000-0000-0000-000000000011",
        feedback_type: "explicit",
        feedback_value: "accepted",
        metadata: '{"ui":"web","reason":"user clicked accept"}'
    };

    // 2. Create AI feedback record (happy path)
    const created = await api.functional.advancedAI.aiFeedback.post(
        connection,
        validInput,
    );
    typia.assert<IAiFeedback>(created);
    TestValidator.equals("feedback_type")(validInput.feedback_type)(created.feedback_type);
    TestValidator.equals("decision_log_id")(validInput.decision_log_id)(created.decision_log_id);
    TestValidator.equals("user_id")(validInput.user_id)(created.user_id);
    TestValidator.equals("feedback_value")(validInput.feedback_value)(created.feedback_value);
    TestValidator.equals("metadata")(validInput.metadata)(created.metadata);
    TestValidator.predicate("created_at should be present")(() => typeof created.created_at === "string" && created.created_at.length > 0);
    TestValidator.predicate("id should be present")(() => typeof created.id === "string" && created.id.length > 0);

    // 3. Attempt to create feedback with missing required fields - missing decision_log_id
    const missingDecisionLog: IAiFeedback.ICreate = {
        // decision_log_id: missing
        user_id: "00000000-0000-0000-0000-000000000022",
        feedback_type: "implicit",
        feedback_value: "auto",
        metadata: null
    } as any;
    await TestValidator.error("Should fail - missing decision_log_id")(() =>
        api.functional.advancedAI.aiFeedback.post(connection, missingDecisionLog),
    );

    // 4. Attempt to create feedback with missing required fields - missing feedback_type
    const missingFeedbackType: IAiFeedback.ICreate = {
        decision_log_id: "00000000-0000-0000-0000-000000000003",
        user_id: null,
        // feedback_type: missing
        feedback_value: "auto",
        metadata: null
    } as any;
    await TestValidator.error("Should fail - missing feedback_type")(() =>
        api.functional.advancedAI.aiFeedback.post(connection, missingFeedbackType),
    );

    // 5. Attempt to create feedback with invalid metadata (metadata must be string or null)
    const invalidMetadata: IAiFeedback.ICreate = {
        decision_log_id: "00000000-0000-0000-0000-000000000004",
        user_id: "00000000-0000-0000-0000-000000000044",
        feedback_type: "explicit",
        feedback_value: "invalid-meta",
        metadata: 12345 as any // Invalid type
    };
    await TestValidator.error("Should fail - invalid metadata type")(() =>
        api.functional.advancedAI.aiFeedback.post(connection, invalidMetadata),
    );

    // 6. Attempt duplicate creation using the same payload as before
    // Should either fail (duplicate constraint) or return the same object (idempotent)
    await TestValidator.error("Should fail or handle idempotency on duplicate creation")(() =>
        api.functional.advancedAI.aiFeedback.post(connection, validInput),
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IAiFeedback } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiFeedback";
import typia from "typia";

import { v4 as uuidv4 } from "uuid";

export async function test_update_ai_feedback_by_id_and_handle_missing_or_invalid_data(
  connection: api.IConnection
): Promise<void> {
  // 1. Create feedback entry for update scenario
  const createInput: IAiFeedback.ICreate = {
    decision_log_id: uuidv4(),
    feedback_type: "explicit",
    // feedback_value and user_id can be omitted or null
    feedback_value: "positive",
    metadata: JSON.stringify({ context: "initial creation for update test" }),
    user_id: uuidv4()
  };
  const created = await api.functional.advancedAI.aiFeedback.post(
    connection,
    createInput
  );
  typia.assert<IAiFeedback>(created);
  TestValidator.equals("feedback_type should be explicit")("explicit")(
    created.feedback_type
  );

  // 2. Prepare update payload
  const updateInput: IAiFeedback.IUpdate = {
    feedback_type: "accepted",
    feedback_value: "negative",
    metadata: JSON.stringify({ change: true })
  };

  // 3. Execute update
  const updated = await api.functional.advancedAI.aiFeedback.put(
    connection,
    created.id,
    updateInput
  );
  typia.assert<IAiFeedback>(updated);
  TestValidator.equals("type updated")("accepted")(updated.feedback_type);
  TestValidator.equals("value updated")("negative")(updated.feedback_value);
  TestValidator.equals("metadata updated")(updateInput.metadata)(updated.metadata);
  TestValidator.equals("id matches")((created.id))(updated.id);

  // 4. Try updating a non-existent record
  await TestValidator.httpError("update on non-existent feedback fails")(404)(async () => {
    await api.functional.advancedAI.aiFeedback.put(
      connection,
      uuidv4(),
      updateInput
    );
  });

  // 5. Update with invalid data: feedback_type is null
  const invalidInput: IAiFeedback.IUpdate = {
    feedback_type: null
  };
  await TestValidator.httpError("update with feedback_type null fails")(400, 422)(async () => {
    await api.functional.advancedAI.aiFeedback.put(
      connection,
      created.id,
      invalidInput
    );
  });
}

