import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/ISecurityEvent";
import { IPageISecurityEvent } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageISecurityEvent";
import typia from "typia";

export async function test_api_list_security_event_logs_with_filter_and_pagination(connection: api.IConnection): Promise<void> {
  // Step 1: Retrieve with no filters
  const res1 = await api.functional.securityCompliance.securityEvent.patch(connection, {});
  typia.assert<IPageISecurityEvent>(res1);
  TestValidator.predicate("Should return at least one security event")(() => Array.isArray(res1.data) && res1.data.length > 0);
  const sampleEvent = res1.data[0];

  // Step 2: Filter by event_type
  if (sampleEvent.event_type) {
    const res2 = await api.functional.securityCompliance.securityEvent.patch(connection, { event_type: sampleEvent.event_type });
    typia.assert<IPageISecurityEvent>(res2);
    TestValidator.predicate("All events should match event_type")(() => res2.data.every(e => e.event_type === sampleEvent.event_type));
  }

  // Step 3: Filter by valid time range
  if (sampleEvent.created_at) {
    const fromTime = sampleEvent.created_at;
    const toTime = sampleEvent.created_at;
    const res3 = await api.functional.securityCompliance.securityEvent.patch(connection, { from_time: fromTime, to_time: toTime });
    typia.assert<IPageISecurityEvent>(res3);
    TestValidator.predicate("Events returned should be within the date range")(() => res3.data.every(e => e.created_at >= fromTime && e.created_at <= toTime));
  }

  // Step 4: Pagination (limit=1)
  const res4 = await api.functional.securityCompliance.securityEvent.patch(connection, { limit: 1 });
  typia.assert<IPageISecurityEvent>(res4);
  TestValidator.equals("Should return exactly one event for limit=1")<number>(1)(res4.data.length);
  TestValidator.equals("Pagination meta should reflect limit=1")<number>(1)(res4.pagination.limit);

  // Step 5: Out-of-range page (very high page number)
  const highPageNumber = 99999;
  const res5 = await api.functional.securityCompliance.securityEvent.patch(connection, { page: highPageNumber, limit: 1 });
  typia.assert<IPageISecurityEvent>(res5);
  TestValidator.equals("Should return empty array for out-of-range page")<number>(0)(res5.data.length);

  // Step 6: Filter by nonsense event_type (should return empty)
  const res6 = await api.functional.securityCompliance.securityEvent.patch(connection, { event_type: "___NO_SUCH_EVENT_TYPE___" });
  typia.assert<IPageISecurityEvent>(res6);
  TestValidator.equals("Should return empty array for nonsense event_type")<number>(0)(res6.data.length);
}

