  const validInput: ISecurityEvent.ICreate = {
    event_type: "login_attempt",
    event_details: "{\"ip\":\"192.168.1.1\",\"success\":true}",
    user_id: "7e09c4bd-6b31-4b22-a75d-e0c8fafddc79",
