  TestValidator.equals("event_type match")(validInput.event_type)(created.event_type);
  TestValidator.equals("event_details match")(validInput.event_details)(created.event_details);
  TestValidator.predicate("id should be non-empty string")(() => typeof created.id === "string" && created.id.length > 0);
  TestValidator.predicate("created_at must exist")(() => typeof created.created_at === "string" && created.created_at.length > 0);

  // 3. Attempt creation with missing event_type
  const missingEventType: ISecurityEvent.ICreate = {
    // @ts-expect-error intentionally omit event_type
    event_details: "details",
    user_id: null,
    session_id: null,
    api_client_id: null
  };
  await TestValidator.httpError("Missing event_type triggers error")(400)(
    () => api.functional.securityCompliance.securityEvent.post(connection, missingEventType)
  );

