    event_type: "token_rotation",
    // @ts-expect-error intentionally omit event_details
    user_id: null,
    session_id: null,
    api_client_id: null
  };
  await TestValidator.httpError("Missing event_details triggers error")(400)(
    () => api.functional.securityCompliance.securityEvent.post(connection, missingDetails)
  );

  // 5. Invalid (empty) event_type
  const emptyType: ISecurityEvent.ICreate = {
    event_type: "",
    event_details: "something happened",
    user_id: null,
    session_id: null,
    api_client_id: null
