    // 2. Update valid record with new details
    const updateInput: ISecurityEvent.IUpdate = {
        event_details: "Updated: User X login was successful, IP validated."
    };
    const updated: ISecurityEvent = await api.functional.securityCompliance.securityEvent.put(connection, created.id, updateInput);
