import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IPermission } from "@ORGANIZATION/PROJECT-api/lib/structures/IPermission";
import { IPageIPermission } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPermission";
import typia from "typia";

export async function test_api_list_and_search_permission_records_with_filters(
  connection: api.IConnection,
): Promise<void> {
  // 1. No filters
  const basic: IPageIPermission = await api.functional.securityCompliance.permission.patch(
    connection,
    {},
  );
  typia.assert<IPageIPermission>(basic);
  TestValidator.equals("nonempty data array or empty array")(
    Array.isArray(basic.data),
  )(() => true);
  TestValidator.equals("pagination type")(
    typeof basic.pagination.current === "number",
  )(() => true);
  // If there are records, save ids for filters
  const hasData = basic.data.length > 0;
  const sample = hasData ? basic.data[0] : null;

  // 2. Filter by code (if data exists)
  if (sample) {
    const codePart = sample.code.slice(0, 3);
    const byCode: IPageIPermission = await api.functional.securityCompliance.permission.patch(
      connection,
      { code: codePart },
    );
    typia.assert<IPageIPermission>(byCode);
    TestValidator.predicate("all results have code containing filter")(() =>
      byCode.data.every((x) => x.code.includes(codePart)),
    );
  }

  // 3. Filter by description (if data exists)
  if (sample) {
    const descPart = sample.description.slice(0, 3);
    const byDesc: IPageIPermission = await api.functional.securityCompliance.permission.patch(
      connection,
      { description: descPart },
    );
    typia.assert<IPageIPermission>(byDesc);
    TestValidator.predicate("all descriptions contain filter")(() =>
      byDesc.data.every((x) => x.description.includes(descPart)),
    );
  }

  // 4. Filter by policy_id (if present and not null)
  if (sample && sample.policy_id) {
    const byPolicy: IPageIPermission = await api.functional.securityCompliance.permission.patch(
      connection,
      { policy_id: sample.policy_id },
    );
    typia.assert<IPageIPermission>(byPolicy);
    TestValidator.predicate("all data has correct policy_id")(() =>
      byPolicy.data.every((x) => x.policy_id === sample.policy_id),
    );
  }

  // 5. Pagination: request second page (assume limit 1)
  const paged: IPageIPermission = await api.functional.securityCompliance.permission.patch(
    connection,
    { limit: 1, page: 2 },
  );
  typia.assert<IPageIPermission>(paged);
  TestValidator.equals("page = 2")(
    paged.pagination.current,
  )(2);
  TestValidator.equals("limit = 1")(
    paged.pagination.limit,
  )(1);

  // 6. Sorting by created_at ascending/descending
  const sortedAsc: IPageIPermission = await api.functional.securityCompliance.permission.patch(
    connection,
    { sort_field: "created_at", sort_direction: "asc" },
  );
  const sortedDesc: IPageIPermission = await api.functional.securityCompliance.permission.patch(
    connection,
    { sort_field: "created_at", sort_direction: "desc" },
  );
  typia.assert<IPageIPermission>(sortedAsc);
  typia.assert<IPageIPermission>(sortedDesc);
  if (sortedAsc.data.length > 1 && sortedDesc.data.length > 1) {
    const ascFirst = sortedAsc.data[0].created_at;
    const descFirst = sortedDesc.data[0].created_at;
    // Should be opposites in order
    TestValidator.predicate("sorting direction works")(() =>
      ascFirst !== descFirst,
    );
  }

  // 7. Negative test: invalid page/limit
  await TestValidator.httpError("page=0 should error or fallback")(400, 422, 500)(async () =>
    api.functional.securityCompliance.permission.patch(connection, { page: 0 }),
  );
  await TestValidator.httpError("limit=-1 should error or fallback")(400, 422, 500)(async () =>
    api.functional.securityCompliance.permission.patch(connection, { limit: -1 }),
  );

  // 8. Negative test: nonsense filter value
  const nonsense: IPageIPermission = await api.functional.securityCompliance.permission.patch(
    connection,
    { code: "nonsensenopeunlikely" },
  );
  typia.assert<IPageIPermission>(nonsense);
  TestValidator.equals("empty array for gibberish search")(
    nonsense.data.length,
  )(0);
}

