export async function test_api_create_permission_record_with_valid_invalid_and_duplicate_data(connection: api.IConnection): Promise<void> {
    // 1. Prepare valid input
    const baseCode = `perm.create.test.${Date.now()}`;
    const validInput: IPermission.ICreate = {
        code: baseCode,
        description: "Test permission for create E2E",
        policy_id: null,
    };

    // 2. Success: create permission
    const created: IPermission = await api.functional.securityCompliance.permission.post(connection, validInput);
    typia.assert<IPermission>(created);
    TestValidator.equals("code")(validInput.code)(created.code);
    TestValidator.equals("description")(validInput.description)(created.description);
    TestValidator.predicate("created id is uuid")(() => /^([0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12})$/i.test(created.id));
    TestValidator.predicate("created_at is ISO date")(() => !isNaN(Date.parse(created.created_at)));
    TestValidator.equals("policy_id is null")(validInput.policy_id)(created.policy_id);

    // 3. Error: attempt duplicate code
    await TestValidator.httpError("duplicate code - unique constraint")(409)(
        () => api.functional.securityCompliance.permission.post(connection, validInput)
    );

    // 4. Error: missing description
    const missingDescription: IPermission.ICreate = {
        code: `${baseCode}_missingdesc`,
        // description is missing
        // @ts-expect-error: intentional
    } as any;
    await TestValidator.httpError("missing description validation")(400, 422)(
        () => api.functional.securityCompliance.permission.post(connection, missingDescription as any)
    );

    // 5. Error: code as empty string
    const emptyCode: IPermission.ICreate = {
        code: "",
        description: "Empty code case",
        policy_id: null,
    };
    await TestValidator.httpError("empty code validation")(400, 422)(
        () => api.functional.securityCompliance.permission.post(connection, emptyCode)
    );

    // 6. Error: null description (should fail)
    const nullDesc: IPermission.ICreate = {
        code: `${baseCode}_nulldesc`,
        description: null as any,
        policy_id: null,
    };
    await TestValidator.httpError("null description validation")(400, 422)(
        () => api.functional.securityCompliance.permission.post(connection, nullDesc)
    );

    // 7. Error: malformed policy_id
    const badPolicyId: IPermission.ICreate = {
        code: `${baseCode}_badpolicy`,
        description: "Malformed policy id",
        policy_id: "not-a-uuid",
    };
    await TestValidator.httpError("malformed policy_id validation")(400, 422)(
        () => api.functional.securityCompliance.permission.post(connection, badPolicyId)
    );
}

