    TestValidator.equals("created is_granted")<boolean>(createInput.is_granted)(created.is_granted);
    TestValidator.equals("created granted_at")<string>(createInput.granted_at)(created.granted_at);
    TestValidator.equals("created revoked_at")(createInput.revoked_at)(created.revoked_at);

    // Step 2: Update the consent (grant revoked)
    const updateInput: ICoreConsent.IUpdate = {
        is_granted: false,
        revoked_at: new Date().toISOString(),
