    TestValidator.equals("updated type (unchanged)")<string>(created.type)(updated.type);
    TestValidator.equals("updated user_id (unchanged)")<string>(created.user_id)(updated.user_id);

    // Step 3: Attempt to update a non-existent consent (should error)
    const fake_id = "aaaaaaaa-aaaa-aaaa-aaaa-aaaaaaaaaaaa";
    await TestValidator.httpError("update non-existent consent should fail")(404)(
        () => api.functional.core.consents.put(connection, fake_id, { is_granted: true })
