    // Step 4: Attempt to update with invalid data (invalid ISO date string)
    await TestValidator.httpError("update with invalid ISO date string should fail")(400, 422)(
        () => api.functional.core.consents.put(connection, created.id, { granted_at: "not-a-date" })
    );
}

