import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { ICoreAccessLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreAccessLog";
import { IPageICoreAccessLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageICoreAccessLog";

export async function test_api_list_access_logs_with_various_filters(connection: api.IConnection): Promise<void> {
  // --- Step 1: Create a valid access log entry
  const testLogInput: ICoreAccessLog.ICreate = {
    user_id: "11111111-1111-4111-8111-111111111111",
    session_id: "22222222-2222-4222-8222-222222222222",
    event_type: "login",
    ip: "192.168.0.1",
    agent: "test-agent",
    success: true,
    occurred_at: new Date().toISOString(),
  };
  const createdLog = await api.functional.core.accessLogs.post(connection, testLogInput);
  typia.assert<ICoreAccessLog>(createdLog);
  TestValidator.equals("access log create: id matches")(createdLog.id.length === 36)(true);

  // --- Step 2: List logs by user_id filter
  const reqByUser: ICoreAccessLog.IRequest = { user_id: testLogInput.user_id };
  const pageByUser = await api.functional.core.accessLogs.patch(connection, reqByUser);
  typia.assert<IPageICoreAccessLog>(pageByUser);
  TestValidator.predicate("should find at least one log for user_id")(() => pageByUser.data.some(l => l.id === createdLog.id));

  // --- Step 3: List logs by combined filter (event_type, ip, time)
  const reqCombo: ICoreAccessLog.IRequest = {
    user_id: testLogInput.user_id,
    event_type: "login",
    ip: testLogInput.ip,
    agent: testLogInput.agent,
    success: true,
    occurred_at_from: new Date(Date.now() - 60 * 1000).toISOString(), // now - 1 minute
    occurred_at_to: new Date(Date.now() + 60 * 1000).toISOString(),   // now + 1 minute
    page: 1,
    limit: 10,
  };
  const pageCombo = await api.functional.core.accessLogs.patch(connection, reqCombo);
  typia.assert<IPageICoreAccessLog>(pageCombo);
  TestValidator.predicate("logs filtered by combo")(() => pageCombo.data.some(l => l.id === createdLog.id));

  // --- Step 4: Invalid filter, expect empty list
  const reqNoMatch: ICoreAccessLog.IRequest = {
    user_id: "99999999-9999-4999-8999-999999999999",
    event_type: "not-an-event",
    page: 1,
    limit: 5,
  };
  const pageEmpty = await api.functional.core.accessLogs.patch(connection, reqNoMatch);
  typia.assert<IPageICoreAccessLog>(pageEmpty);
  TestValidator.equals("empty search")([] as ICoreAccessLog[])(pageEmpty.data);

  // --- Step 5: Invalid input cases (malformed UUID, negative page/limit)
  await TestValidator.error("malformed uuid rejects")(() =>
    api.functional.core.accessLogs.patch(connection, { user_id: "not-a-uuid" })
  );
  await TestValidator.error("negative page rejects")(() =>
    api.functional.core.accessLogs.patch(connection, { page: -1 })
  );
  await TestValidator.error("zero limit rejects")(() =>
    api.functional.core.accessLogs.patch(connection, { limit: 0 })
  );

  // --- Step 6: High volume paging test
  // Insert 101 logs for the same user_id, unique IP/agent per log
  const volumeUserId = "cccccccc-cccc-4ccc-8ccc-cccccccccccc";
  const now = Date.now();
  const volumeInputs: ICoreAccessLog.ICreate[] = Array.from({ length: 101 }, (_, i) => ({
    user_id: volumeUserId,
    session_id: null,
    event_type: "api_call",
    ip: `10.0.0.${i+1}`,
    agent: `agent-${i+1}`,
    success: i % 2 === 0,
    occurred_at: new Date(now + i * 1000).toISOString(),
  }));
  for (const v of volumeInputs) {
    await api.functional.core.accessLogs.post(connection, v);
  }
  // Page 1: limit 50
  const volPage1 = await api.functional.core.accessLogs.patch(connection, { user_id: volumeUserId, page: 1, limit: 50 });
  typia.assert<IPageICoreAccessLog>(volPage1);
  TestValidator.equals("page 1 size")(50)(volPage1.data.length);
  // Page 3: limit 50 (should have 1 log, since 101 total)
  const volPage3 = await api.functional.core.accessLogs.patch(connection, { user_id: volumeUserId, page: 3, limit: 50 });
  typia.assert<IPageICoreAccessLog>(volPage3);
  TestValidator.equals("page 3 size")(1)(volPage3.data.length);
  TestValidator.equals("pagination page count")(3)(volPage3.pagination.pages);
}

