import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { ICoreAccessLog } from "@ORGANIZATION/PROJECT-api/lib/structures/ICoreAccessLog";
import typia from "typia";

export async function test_create_access_log_with_required_and_invalid_fields(
  connection: api.IConnection,
): Promise<void> {
  // 1. Valid creation
  const validRequest: ICoreAccessLog.ICreate = {
    user_id: "a1111111-1111-1111-1111-111111111111",
    session_id: null,
    event_type: "login",
    ip: "192.168.1.100",
    agent: "Mozilla/5.0 (Windows NT 10.0; Win64; x64)",
    success: true,
    occurred_at: new Date().toISOString(),
  };
  const created = await api.functional.core.accessLogs.post(connection, validRequest);
  typia.assert<ICoreAccessLog>(created);
  TestValidator.equals("user_id")(validRequest.user_id)(created.user_id);
  TestValidator.equals("event_type")(validRequest.event_type)(created.event_type);
  TestValidator.equals("ip")(validRequest.ip)(created.ip);
  TestValidator.equals("agent")(validRequest.agent)(created.agent);
  TestValidator.equals("success")(validRequest.success)(created.success);
  TestValidator.equals("occurred_at")(validRequest.occurred_at)(created.occurred_at);
  TestValidator.predicate("id must be a uuid v4")(() =>
    /^[0-9a-fA-F\-]{36}$/.test(created.id),
  );

  // 2. Missing required fields (no user_id)
  await TestValidator.httpError("missing user_id gives 400")(400)(async () => {
    const req = { ...validRequest };
    // @ts-expect-error purposely missing user_id
    delete req.user_id;
    return await api.functional.core.accessLogs.post(connection, req);
  });

  // 3. Malformed data: invalid UUID
  await TestValidator.httpError("bad user_id uuid")(400)(async () => {
    const req = { ...validRequest, user_id: "not-a-uuid" };
    return await api.functional.core.accessLogs.post(connection, req);
  });

  // 3-2. Invalid occurred_at format
  await TestValidator.httpError("bad occurred_at format")(400)(async () => {
    const req = { ...validRequest, occurred_at: "not-a-date" };
    return await api.functional.core.accessLogs.post(connection, req);
  });

  // 3-3. String instead of boolean for success
  await TestValidator.httpError("bad success type")(400)(async () => {
    const req = { ...validRequest, success: "yes" as any };
    return await api.functional.core.accessLogs.post(connection, req);
  });

  // 4. Duplicate log event
  await TestValidator.httpError("duplicate event denied")(409, 400)(async () => {
    return await api.functional.core.accessLogs.post(connection, validRequest);
  });
}

