    const creatorId = "76b32846-3942-4f71-ba87-37cec0aa5aee";
    const initial: IProductCatalogProduct.ICreate = {
        name: "Test Product",
        description: "Initial product description.",
        internal_code: "TP-001",
        status: "draft",
        created_by_user_id: creatorId,
        updated_by_user_id: creatorId
    };
    const created = await api.functional.productCatalog.products.post(connection, initial);
    typia.assert<IProductCatalogProduct>(created);

    // 2. Update allowed fields
    const updaterId = "d20e14b1-acfb-41a9-bacf-e909be99eb9b";
    const update: IProductCatalogProduct.IUpdate = {
        updated_by_user_id: updaterId,
        name: "Updated Test Product",
        description: "Updated product description.",
        internal_code: "TP-002",
        status: "active"
    };
    const updated = await api.functional.productCatalog.products.put(connection, created.id, update);
    typia.assert<IProductCatalogProduct>(updated);
    TestValidator.equals("updated name")(update.name!)(updated.name);
    TestValidator.equals("updated description")(update.description!)(updated.description);
    TestValidator.equals("updated internal_code")(update.internal_code!)(updated.internal_code);
    TestValidator.equals("updated status")(update.status!)(updated.status);
    TestValidator.equals("updater id")(update.updated_by_user_id)(updated.updated_by_user_id);
    TestValidator.equals("id unchanged")(created.id)(updated.id);
    TestValidator.equals("creator id unchanged")(creatorId)(updated.created_by_user_id);
    TestValidator.predicate("created_at remains valid")(() => typeof updated.created_at === "string" && updated.created_at.length > 0);
    TestValidator.predicate("updated_at updated")(() => updated.updated_at !== created.updated_at);

    // 3. Attempt to update non-existent product (expect error)
    await TestValidator.httpError("update non-existent product")(404)(async () => {
        await api.functional.productCatalog.products.put(
            connection,
            "63a9d80c-0ef4-11e8-ba89-0ed5f89f718b", // unlikely to exist
            update
        );
    });

    // 4. Attempt update with missing required field (updated_by_user_id)
    const missingUpdater: Partial<IProductCatalogProduct.IUpdate> = {
        name: "Invalid update (missing updater)",
    };
    await TestValidator.httpError("missing updated_by_user_id triggers validation")(400)(async () => {
        // Type assertion bypass to simulate malformed input -- in real world test infra would allow this at transport
        // @ts-expect-error
        await api.functional.productCatalog.products.put(connection, created.id, missingUpdater);
    });
}

