import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import { IPageIProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductCatalogProductVariant";

export async function test_list_product_variants_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
  // 1. Create a product variant
  const variantInput: IProductCatalogProductVariant.ICreate = {
    product_id: crypto.randomUUID(),
    import_feed_id: crypto.randomUUID(),
    sku: `TESTSKU-${Math.random().toString(36).substring(2,8)}`,
    price: 99.95,
    barcode: `BAR-${Math.random().toString(36).substring(2,8)}`,
    available: true,
  };
  const created = await api.functional.productCatalog.productVariants.post(connection, variantInput);
  typia.assert<IProductCatalogProductVariant>(created);

  // 2. Successful filter by product_id
  const searchByProductId = await api.functional.productCatalog.productVariants.patch(
    connection,
    { product_id: created.product_id, limit: 10, offset: 0 }
  );
  typia.assert<IPageIProductCatalogProductVariant>(searchByProductId);
  TestValidator.predicate("should return at least 1 result")(
    searchByProductId.data.some(v => v.id === created.id)
  );
  TestValidator.equals("product_id filter")(
    created.product_id
  )(searchByProductId.data[0]?.product_id ?? "");

  // 3. No result with absurd SKU
  const emptySku = await api.functional.productCatalog.productVariants.patch(
    connection,
    { sku: "NOSUCHSKU-XYZ", limit: 10, offset: 0 }
  );
  typia.assert<IPageIProductCatalogProductVariant>(emptySku);
  TestValidator.equals("no variant for bogus SKU")(
    [] as IProductCatalogProductVariant[]
  )(emptySku.data);

  // 4. Pagination - create a second variant with same product_id
  const variantInput2: IProductCatalogProductVariant.ICreate = {
    ...variantInput,
    sku: `TESTSKU2-${Math.random().toString(36).substring(2,8)}`,
    barcode: `BAR2-${Math.random().toString(36).substring(2,8)}`,
  };
  const created2 = await api.functional.productCatalog.productVariants.post(connection, variantInput2);
  typia.assert<IProductCatalogProductVariant>(created2);

  // List both variants, get page 1 (offset 0, limit 1)
  const page1 = await api.functional.productCatalog.productVariants.patch(
    connection,
    { product_id: created.product_id, limit: 1, offset: 0 },
  );
  typia.assert<IPageIProductCatalogProductVariant>(page1);
  TestValidator.equals("page size 1")(1)(page1.data.length);
  TestValidator.equals("total records >=2")(
    page1.pagination.records >= 2
  )(true);
  // Get page 2 (offset 1, limit 1)
  const page2 = await api.functional.productCatalog.productVariants.patch(
    connection,
    { product_id: created.product_id, limit: 1, offset: 1 },
  );
  typia.assert<IPageIProductCatalogProductVariant>(page2);
  TestValidator.equals("page 2 size 1")(1)(page2.data.length);
  TestValidator.predicate("page 2 not same as page 1")(
    page2.data[0]?.id !== page1.data[0]?.id
  );

  // 5. Error case: invalid filter types
  await TestValidator.httpError("invalid 'available' string type")(400)(
    () => api.functional.productCatalog.productVariants.patch(
      connection,
      { available: "notabool" as unknown as boolean }
    )
  );
  await TestValidator.httpError("negative limit") (400)(
    () => api.functional.productCatalog.productVariants.patch(
      connection,
      { limit: -5 }
    )
  );
  // Edge: extra unexpected field
  await TestValidator.httpError("extra filter property")(400)(
    () => api.functional.productCatalog.productVariants.patch(
      connection,
      { random_nonexistent_field: "oops" } as any
    )
  );
}

