import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCatalogProduct } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProduct";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import typia from "typia";

export async function test_api_create_product_variant_with_complete_and_invalid_payloads(connection: api.IConnection): Promise<void> {
  // Step 1: Create parent product
  const productInput = {
    created_by_user_id: "11111111-1111-1111-1111-111111111111",
    updated_by_user_id: "11111111-1111-1111-1111-111111111111",
    name: "Test Phone",
    description: "Test product description for variant creation.",
    internal_code: `TEST-PROD-${Date.now()}`,
    status: "active",
  };
  const product = await api.functional.productCatalog.products.post(connection, productInput);
  typia.assert<IProductCatalogProduct>(product);
  TestValidator.predicate("product id present")(() => typeof product.id === "string" && !!product.id);

  // Step 2: Create valid product variant
  const variantInput = {
    product_id: product.id,
    sku: `SKU-${Date.now()}`,
    price: 199900,
    barcode: `BAR-${Date.now()}`,
    available: true,
  };
  const createdVariant = await api.functional.productCatalog.productVariants.post(connection, variantInput);
  typia.assert<IProductCatalogProductVariant>(createdVariant);
  TestValidator.equals("product_id linkage")(variantInput.product_id)(createdVariant.product_id);
  TestValidator.predicate("created variant id present")(() => typeof createdVariant.id === "string" && !!createdVariant.id);
  TestValidator.predicate("created_at field exists")(() => typeof createdVariant.created_at === "string" && !!createdVariant.created_at);

  // Step 3: Negative case - missing required fields (e.g., sku)
  const incompleteVariant = {
    product_id: product.id,
    price: 199900,
    barcode: `BAR2-${Date.now()}`,
    available: true,
  };
  await TestValidator.httpError("missing sku triggers validation error")(400)(
    () => api.functional.productCatalog.productVariants.post(connection, incompleteVariant as any)
  );

  // Step 4: Negative case - invalid field type (price as string)
  const invalidTypeVariant = {
    product_id: product.id,
    sku: `SKU-invalid-${Date.now()}`,
    price: "WRONG_TYPE", // should be number
    barcode: `BAR3-${Date.now()}`,
    available: true,
  };
  await TestValidator.httpError("invalid price type triggers validation error")(400)(
    () => api.functional.productCatalog.productVariants.post(connection, invalidTypeVariant as any)
  );

  // Step 5: Negative case - duplicate SKU/barcode for the same product
  const duplicateVariant = {
    product_id: product.id,
    sku: variantInput.sku,
    price: 199900,
    barcode: variantInput.barcode,
    available: true,
  };
  await TestValidator.httpError("duplicate SKU/barcode triggers uniqueness constraint")(400, 409)(
    () => api.functional.productCatalog.productVariants.post(connection, duplicateVariant)
  );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCatalogProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCatalogProductVariant";
import { IProductVariant } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductVariant";
import typia from "typia";

export async function test_update_product_variant_and_audit_changes(connection: api.IConnection): Promise<void> {
  // 1. Precondition: Create a product variant (required for update)
  const createInput: IProductCatalogProductVariant.ICreate = {
    product_id: "d81e3bfc-7e4b-428a-b10b-c074a663f68d", // sample UUID, must exist in test DB
    import_feed_id: "c85d3295-db55-4623-b0a5-af414610c452", // or null if not using import feed
    sku: `SKU-PREUPDATE-${Date.now()}`,
    price: 1000,
    barcode: `BARCODE-PREUPDATE-${Date.now()}`,
    available: true
  };
  const created: IProductCatalogProductVariant = await api.functional.productCatalog.productVariants.post(connection, createInput);
  typia.assert<IProductCatalogProductVariant>(created);

  // 2. Positive: Update allowed fields (e.g., price, available)
  const updateInput: IProductVariant.IUpdate = {
    price: 1450,
    available: false,
    sku: `${created.sku}-UPDATED`,
  };
  const updated: IProductVariant = await api.functional.productCatalog.productVariants.put(connection, created.id, updateInput);
  typia.assert<IProductVariant>(updated);
  TestValidator.equals("updated.id")(created.id)(updated.id);
  TestValidator.equals("updated.price")(updateInput.price)(updated.price);
  TestValidator.equals("updated.available")(updateInput.available)(updated.available);
  TestValidator.equals("updated.sku")(updateInput.sku)(updated.sku);
  // updated_at must be changed after update
  TestValidator.predicate("updated_at changed")(() => new Date(updated.updated_at) > new Date(created.updated_at));

  // 3. Negative: Update with invalid price type (string instead of number) - expect 400
  await TestValidator.httpError("invalid price type")(400)(async () => {
    const invalidInput: IProductVariant.IUpdate = {
      price: "INVALID" as unknown as number,
    };
    return api.functional.productCatalog.productVariants.put(connection, created.id, invalidInput);
  });

  // 4. Negative: Update non-existent ID (expect 404)
  await TestValidator.httpError("update non-existent id")(404)(async () => {
    const fakeId = "c1746dda-6d39-44d6-b6da-3128e77f999a"; // UUID assumed not to exist
    return api.functional.productCatalog.productVariants.put(connection, fakeId, updateInput);
  });
}

