export async function test_api_delete_product_variant_and_validate_referential_integrity(connection: api.IConnection): Promise<void> {
    // Step 1: Prepare input data
    const unique = Date.now().toString();
    const input: IProductCatalogProductVariant.ICreate = {
        product_id: "11111111-1111-4111-8111-1111111111a1", // dummy parent, should be a valid UUID in real system
        import_feed_id: null,
        sku: `sku-delete-${unique}`,
        price: 100 + Math.random(),
        barcode: `barcode-delete-${unique}`,
        available: true,
    };

    // Step 2: Create product variant
    const created = await api.functional.productCatalog.productVariants.post(connection, input);
    typia.assert<IProductCatalogProductVariant>(created);
    TestValidator.equals("product variant create: sku")(input.sku)(created.sku);
    TestValidator.equals("product variant create: not deleted")(true)(created.available);

    // Step 4: Soft-delete
    const deleted = await api.functional.productCatalog.productVariants.delete(connection, created.id);
    typia.assert<{ id: string; status: string; deleted_at: string }>(deleted);
    TestValidator.equals("soft-delete: id matches")(created.id)(deleted.id);
