import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCategory";
import { IPageIProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductCategory";

export async function test_list_product_categories_with_filters_and_hierarchy_check(connection: api.IConnection): Promise<void> {
  // 1. Create root category
  const rootInput: IProductCategory.ICreate = {
    name: "RootCategory",
    parent_id: null,
  };
  const root = await api.functional.productCatalog.productCategories.post(connection, rootInput);
  typia.assert<IProductCategory>(root);
  TestValidator.equals("root name")("RootCategory")(root.name);
  TestValidator.equals("root parent_id")(null)(root.parent_id);

  // 2. Create sub-category under root
  const subInput: IProductCategory.ICreate = {
    name: "ChildCategory",
    parent_id: root.id,
  };
  const sub = await api.functional.productCatalog.productCategories.post(connection, subInput);
  typia.assert<IProductCategory>(sub);
  TestValidator.equals("child name")("ChildCategory")(sub.name);
  TestValidator.equals("child parent_id")(root.id)(sub.parent_id);

  // 3. List all categories (no filters)
  const allList = await api.functional.productCatalog.productCategories.patch(connection, {});
  typia.assert<IPageIProductCategory>(allList);
  TestValidator.equals("list pagination > count")(2)(allList.pagination.records);
  TestValidator.equals("list all > length")(2)(allList.data.length);
  // returned ids
  const allIds = allList.data.map((c) => c.id);
  TestValidator.predicate("all contains root id")(() => allIds.includes(root.id));
  TestValidator.predicate("all contains child id")(() => allIds.includes(sub.id));

  // 4. List categories filtered by root parent_id (should return only the sub-category)
  const childrenOfRoot = await api.functional.productCatalog.productCategories.patch(connection, {
    parent_id: root.id,
  });
  typia.assert<IPageIProductCategory>(childrenOfRoot);
  TestValidator.equals("child search count")(1)(childrenOfRoot.data.length);
  TestValidator.equals("child correct id")(sub.id)(childrenOfRoot.data[0].id);

  // 5. List categories using partial name search
  const nameSearch = await api.functional.productCatalog.productCategories.patch(connection, {
    name: "RootCat",
  });
  typia.assert<IPageIProductCategory>(nameSearch);
  TestValidator.equals("nameSearch count")(1)(nameSearch.data.length);
  TestValidator.equals("nameSearch id")(root.id)(nameSearch.data[0].id);

  // 6. Test paging: limit=1, offset=0 (first page), offset=1 (second page)
  const page1 = await api.functional.productCatalog.productCategories.patch(connection, {
    offset: 0,
    limit: 1,
  });
  typia.assert<IPageIProductCategory>(page1);
  TestValidator.equals("page1 length")(1)(page1.data.length);

  const page2 = await api.functional.productCatalog.productCategories.patch(connection, {
    offset: 1,
    limit: 1,
  });
  typia.assert<IPageIProductCategory>(page2);
  TestValidator.equals("page2 length")(1)(page2.data.length);
  TestValidator.predicate("page1 and page2 ids differ")(() => page1.data[0].id !== page2.data[0].id);

  // 7. Edge: Filter with name that matches nothing
  const none = await api.functional.productCatalog.productCategories.patch(connection, { name: "xxxxxx_nomatch" });
  typia.assert<IPageIProductCategory>(none);
  TestValidator.equals("no match empty array")([] as IProductCategory[])(none.data);

  // 8. Overflown offset
  const overOffset = await api.functional.productCatalog.productCategories.patch(connection, { offset: 100, limit: 10 });
  typia.assert<IPageIProductCategory>(overOffset);
  TestValidator.equals("over offset empty array")([] as IProductCategory[])(overOffset.data);

  // 9. Negative: invalid param type (offset as string)
  await TestValidator.httpError("invalid offsert type")(400)(async () => {
    // @ts-expect-error purposely sending wrong type
    return api.functional.productCatalog.productCategories.patch(connection, { offset: "zero" });
  });

  await TestValidator.httpError("invalid limit type")(400)(async () => {
    // @ts-expect-error purposely sending wrong type
    return api.functional.productCatalog.productCategories.patch(connection, { limit: "one" });
  });

  // 10. Validate pagination fields exist and are reasonable
  for (const pgData of [allList, childrenOfRoot, nameSearch, page1, page2]) {
    TestValidator.predicate("pagination has current/limit/pages")(() => {
      const p = pgData.pagination;
      return typeof p.current === "number" && typeof p.limit === "number" && typeof p.pages === "number";
    });
    TestValidator.predicate("pagination limits positive")(() => {
      const p = pgData.pagination;
      return p.limit > 0 && p.pages > 0;
    });
  }
}

