export async function test_api_create_product_category_and_validate_hierarchy_and_uniqueness(connection: api.IConnection): Promise<void> {
    // 1. Create a root category (atomic, no parent_id)
    const rootInput: IProductCategory.ICreate = {
        name: "Test Root Category",
        parent_id: null,
    };
    const rootCategory = await api.functional.productCatalog.productCategories.post(connection, { body: rootInput });
