export async function test_api_update_product_category_and_validate_relationships(
  connection: api.IConnection,
): Promise<void> {
  // Step 1: Create initial categories
  const rootInput: IProductCategory.ICreate = {
    name: "RootCategory1",
    parent_id: null,
  };
  const rootCategory = await api.functional.productCatalog.productCategories.post(
    connection,
    rootInput,
  );
  typia.assert<IProductCategory>(rootCategory);

  const parentInput: IProductCategory.ICreate = {
    name: "RootCategory2",
    parent_id: null,
  };
  const parentCategory = await api.functional.productCatalog.productCategories.post(
    connection,
    parentInput,
  );
  typia.assert<IProductCategory>(parentCategory);

  const childInput: IProductCategory.ICreate = {
    name: "ChildCategory",
    parent_id: parentCategory.id,
  };
  const childCategory = await api.functional.productCatalog.productCategories.post(
    connection,
    childInput,
  );
  typia.assert<IProductCategory>(childCategory);

  // Step 2: Update name and parent_id for rootCategory -> make it a child of parentCategory
  const updateInput: IProductCategory.IUpdate = {
    name: "UpdatedRootCategory1",
    parent_id: parentCategory.id,
  };
  const updatedCategory = await api.functional.productCatalog.productCategories.put(
    connection,
    rootCategory.id,
    updateInput,
  );
