    await api.functional.productCatalog.productCategories.put(
      connection,
      nonExistentId,
      { name: "Imposter" },
    );
  });

  // Step 4: Negative case - update to duplicate name
  await TestValidator.httpError("update to duplicate name")(400)(async () => {
    await api.functional.productCatalog.productCategories.put(
      connection,
      parentCategory.id,
