    await api.functional.productCatalog.productCategories.put(
      connection,
      rootCategory.id,
      { parent_id: uuidv4() },
    );
  });

  // Step 6: Circular parent_id (attempt to set child as parent of its ancestor)
  await TestValidator.httpError("circular parent_id")(400)(async () => {
