import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IProductCategory } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductCategory";
import typia from "typia";

export async function test_api_delete_product_category_and_ensure_integrity(connection: api.IConnection): Promise<void> {
    // 1. Create parent/root category
    const parentInput: IProductCategory.ICreate = {
        name: `RootCategory_${Date.now()}`,
    };
    const parent = await api.functional.productCatalog.productCategories.post(connection, parentInput);
    typia.assert<IProductCategory>(parent);

    // 2. Successfully soft-delete the created category
    const deleted = await api.functional.productCatalog.productCategories.delete(connection, parent.id);
    typia.assert<IProductCategory.IDeleteResult>(deleted);
    TestValidator.equals("id match after deletion")(parent.id)(deleted.id);
    TestValidator.predicate("deleted_at must be valid ISO string")(() => typeof deleted.deleted_at === "string" && !isNaN(Date.parse(deleted.deleted_at)));
    // evidence is optional, if present must be string
    if ("evidence" in deleted && deleted.evidence !== undefined) {
        TestValidator.predicate("evidence must be a string")(() => typeof deleted.evidence === "string");
    }

    // 3. Attempt deleting same category again (should fail)
    await TestValidator.httpError("deleting already deleted category should fail")(404, 400, 409)(() =>
        api.functional.productCatalog.productCategories.delete(connection, parent.id)
    );

    // 4. Attempt deleting a non-existent category
    const nonExistentId = "00000000-0000-4000-8000-0000000000FF";
    await TestValidator.httpError("deleting non-existent category should fail")(404, 400)(() =>
        api.functional.productCatalog.productCategories.delete(connection, nonExistentId)
    );

    // 5. Create parent + child, try deleting parent (referential integrity)
    const parentWithChildrenInput: IProductCategory.ICreate = {
        name: `ParentWithChild_${Date.now()}`,
    };
    const parentWithChildren = await api.functional.productCatalog.productCategories.post(connection, parentWithChildrenInput);
    typia.assert<IProductCategory>(parentWithChildren);

    const childInput: IProductCategory.ICreate = {
        name: `ChildCategory_${Date.now()}`,
        parent_id: parentWithChildren.id,
    };
    const child = await api.functional.productCatalog.productCategories.post(connection, childInput);
    typia.assert<IProductCategory>(child);
    TestValidator.equals("child's parent_id matches parent")(parentWithChildren.id)(child.parent_id!);
    // Attempt to delete parent, should fail (409 due to child dependency)
    await TestValidator.httpError("should fail to delete parent with child category")(409)(() =>
        api.functional.productCatalog.productCategories.delete(connection, parentWithChildren.id)
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IProductOption } from "@ORGANIZATION/PROJECT-api/lib/structures/IProductOption";
import { IPageIProductOption } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIProductOption";

export async function test_api_list_product_options_with_filtering_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create product options
  const productIdA = crypto.randomUUID();
  const productIdB = crypto.randomUUID();
  const optionA: IProductOption.ICreate = {
    product_id: productIdA,
    option_type: "color",
    value: "Red",
  };
  const optionB: IProductOption.ICreate = {
    product_id: productIdB,
    option_type: "size",
    value: "XL",
  };
  const createdA = await api.functional.productCatalog.productOptions.post(connection, optionA);
  const createdB = await api.functional.productCatalog.productOptions.post(connection, optionB);
  typia.assert<IProductOption>(createdA);
  typia.assert<IProductOption>(createdB);

  // 2. List all (no filters)
  const listResp = await api.functional.productCatalog.productOptions.patch(
    connection,
    {},
  );
  typia.assert<IPageIProductOption>(listResp);
  // Both created options should be in the list
  TestValidator.predicate("all options present")(() =>
    listResp.data.some((o) => o.id === createdA.id) &&
    listResp.data.some((o) => o.id === createdB.id),
  );

  // 3. Filter by product_id (should only get matching)
  const filteredByProduct = await api.functional.productCatalog.productOptions.patch(connection, { product_id: createdA.product_id });
  typia.assert<IPageIProductOption>(filteredByProduct);
  TestValidator.predicate("single product_id filter")(() =>
    filteredByProduct.data.every((o) => o.product_id === createdA.product_id)
  );
  TestValidator.predicate("filtered product included")(() =>
    filteredByProduct.data.some((o) => o.id === createdA.id)
  );

  // 4. Filter by option_type
  const filteredByType = await api.functional.productCatalog.productOptions.patch(connection, { option_type: "color" });
  typia.assert<IPageIProductOption>(filteredByType);
  TestValidator.predicate("option_type filter")(() =>
    filteredByType.data.every((o) => o.option_type === "color")
  );

  // 5. Filter by value
  const filteredByValue = await api.functional.productCatalog.productOptions.patch(connection, { value: "Red" });
  typia.assert<IPageIProductOption>(filteredByValue);
  TestValidator.predicate("value filter - Red")(() =>
    filteredByValue.data.every((o) => o.value === "Red")
  );

  // 6. Pagination
  const pagedResp1 = await api.functional.productCatalog.productOptions.patch(connection, { limit: 1, page: 1 });
  typia.assert<IPageIProductOption>(pagedResp1);
  TestValidator.equals("page 1 length")([1])(pagedResp1.data.length);

  const pagedResp2 = await api.functional.productCatalog.productOptions.patch(connection, { limit: 1, page: 2 });
  typia.assert<IPageIProductOption>(pagedResp2);
  TestValidator.equals("page 2 length")([1])(pagedResp2.data.length);
  // The paged data should not overlap by id
  TestValidator.predicate("pagination non-overlap")(() =>
    pagedResp1.data[0]?.id !== pagedResp2.data[0]?.id
  );

  // 7. Empty results (nonexistent filter)
  const emptyResp = await api.functional.productCatalog.productOptions.patch(connection, {
    product_id: crypto.randomUUID(),
    value: "NonexistentValue",
  });
  typia.assert<IPageIProductOption>(emptyResp);
  TestValidator.equals("empty result")([] as IProductOption[])(emptyResp.data);

  // 8. Invalid filter (malformed uuid)
  await TestValidator.httpError("invalid product_id format")(400)(() =>
    api.functional.productCatalog.productOptions.patch(connection, { product_id: "not-a-uuid" }),
  );
}

