import typia from "typia";

import { randomUUID } from "crypto";

export async function test_api_payment_audit_soft_delete_and_negative_cases(connection: api.IConnection): Promise<void> {
    // 1. Create a payment audit record
    const now = new Date().toISOString();
    const createInput: IPaymentAudit.ICreate = {
        entity_type: "payment",
        entity_id: randomUUID(),
        action: "update",
        user_id: randomUUID(),
        old_value: "beforeState",
        new_value: "afterState",
        timestamp: now,
    };
    const created = await api.functional.paymentsDiscounts.paymentAudits.post(connection, createInput);
    typia.assert<IPaymentAudit>(created);
    const auditId = created.id;

    // 2. Soft-delete the created audit record
    const deleted = await api.functional.paymentsDiscounts.paymentAudits.delete(connection, auditId);
    typia.assert<IPaymentAudit.IDeleteResult>(deleted);
    TestValidator.equals("deleted id")(auditId)(deleted.id);
    TestValidator.equals("deleted_at not null")(false)(!deleted.deleted_at);

