import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IMessageThread } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessageThread";
import { IMessageThread as IMessageThreadType } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessageThread";
import typia from "typia";

export async function test_api_list_message_threads_with_filters_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a thread for testing (with known creator and subject)
  const created_by_user_id =
    "11111111-1111-4111-8111-111111111111"; // example uuid
  const testSubject =
    "Test Subject - Pagination & Filtering";
  const createInput: IMessageThreadType.ICreate = {
    created_by_user_id,
    subject: testSubject,
    archived: false,
  };
  const created = await api.functional.communicationReview.messageThreads.post(
    connection,
    createInput,
  );
  typia.assert<IMessageThread>(created);
  TestValidator.equals("created.subject")(testSubject)(created.subject);
  TestValidator.equals("created.created_by_user_id")(created_by_user_id)(created.created_by_user_id);
  TestValidator.predicate("created.archived")(created.archived === false);

  // 2. List (search) with pagination, default params
  const reqDefault: IMessageThreadType.IRequest = { limit: 1 };
  const resDefault = await api.functional.communicationReview.messageThreads.patch(
    connection,
    reqDefault,
  );
  typia.assert<{ pagination: any; data: IMessageThread[] }>(resDefault);
  TestValidator.predicate("at least one result")(resDefault.data.length >= 1);
  TestValidator.predicate("pagination: page 1")(resDefault.pagination.current === 1);
  TestValidator.predicate("pagination: limit")(resDefault.pagination.limit === 1);

  // 3. Filter by known creator and subject
  const reqFilter: IMessageThreadType.IRequest = {
    created_by_user_id,
    subject: testSubject,
    limit: 10,
  };
  const resFilter = await api.functional.communicationReview.messageThreads.patch(
    connection,
    reqFilter,
  );
  typia.assert<{ pagination: any; data: IMessageThread[] }>(resFilter);
  TestValidator.predicate("filtered includes only expected")(
    resFilter.data.every(
      (t) => t.created_by_user_id === created_by_user_id && t.subject.includes(testSubject),
    ),
  );
  TestValidator.predicate("filtered not empty")(resFilter.data.length >= 1);
  TestValidator.predicate("filtered pagination page 1")(
    resFilter.pagination.current === 1,
  );

  // 4. Search with no-result filter (unique subject)
  const reqNone: IMessageThreadType.IRequest = {
    subject: "non-existent-thread-subject-zzzzzzzz",
  };
  const resNone = await api.functional.communicationReview.messageThreads.patch(
    connection,
    reqNone,
  );
  typia.assert<{ pagination: any; data: IMessageThread[] }>(resNone);
  TestValidator.equals("empty result")([] as IMessageThread[])(resNone.data);
  TestValidator.predicate("empty: zero records")(
    resNone.pagination.records === 0,
  );
  TestValidator.predicate("empty: zero pages")(
    resNone.pagination.pages === 0,
  );

  // 5. Extreme/edge: request a page beyond last available page
  const reqExtreme: IMessageThreadType.IRequest = {
    page: 9999,
    limit: 1,
  };
  const resExtreme = await api.functional.communicationReview.messageThreads.patch(
    connection,
    reqExtreme,
  );
  typia.assert<{ pagination: any; data: IMessageThread[] }>(resExtreme);
  TestValidator.equals("beyond last page is empty")([] as IMessageThread[])(
    resExtreme.data,
  );

  // 6. Archived filter (should not retrieve our new thread with archived true)
  const reqArchived: IMessageThreadType.IRequest = {
    archived: true,
    subject: testSubject,
  };
  const resArchived = await api.functional.communicationReview.messageThreads.patch(
    connection,
    reqArchived,
  );
  typia.assert<{ pagination: any; data: IMessageThread[] }>(resArchived);
  TestValidator.predicate("archived returns no active thread")(
    resArchived.data.every((t) => t.archived === true),
  );

  // 7. Date-ranges: impossible dates (far past and far future)
  const reqImpossible: IMessageThreadType.IRequest = {
    created_from: "2000-01-01T00:00:00Z",
    created_to: "2000-01-02T00:00:00Z",
  };
  const resImpossible = await api.functional.communicationReview.messageThreads.patch(
    connection,
    reqImpossible,
  );
  typia.assert<{ pagination: any; data: IMessageThread[] }>(resImpossible);
  TestValidator.equals("far past is empty")([] as IMessageThread[])(
    resImpossible.data,
  );
}

