import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import typia from "typia";
import { IMessage } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessage";

export async function test_update_message_valid_invalid_and_not_found(connection: api.IConnection): Promise<void> {
    // 1. Create a valid message for updating
    const createInput: IMessage.ICreate = {
        thread_id: "11111111-1111-1111-1111-111111111111",
        content: "Original message content.",
        message_type: "user",
        parent_message_id: null
    };
    const created: IMessage = await api.functional.communicationReview.messages.post(connection, createInput);
    typia.assert<IMessage>(created);

    // 2. Valid update
    const updateInput: IMessage.IUpdate = {
        content: "Updated message content!",
        message_type: "system"
    };
    const updated: IMessage = await api.functional.communicationReview.messages.put(connection, created.id, updateInput);
    typia.assert<IMessage>(updated);
    TestValidator.equals("id unchanged")(created.id)(updated.id);
    TestValidator.equals("content updated")(updateInput.content!)(updated.content);
    TestValidator.equals("message_type updated")(updateInput.message_type!)(updated.message_type);
    TestValidator.equals("thread_id unchanged")(created.thread_id)(updated.thread_id);
    TestValidator.equals("sender_user_id unchanged")(created.sender_user_id)(updated.sender_user_id);
    TestValidator.equals("archived unchanged")(created.archived)(updated.archived);
    TestValidator.predicate("sent_at stays valid")(() => typeof updated.sent_at === "string" && updated.sent_at.length > 0);

    // 3. Try invalid content (empty)
    const invalidContentInput: IMessage.IUpdate = { content: "" };
    await TestValidator.httpError("empty content rejected")(400)(() =>
        api.functional.communicationReview.messages.put(connection, created.id, invalidContentInput)
    );

    // 4. Try invalid message_type
    const invalidTypeInput: IMessage.IUpdate = { message_type: "hack" } as any;
    await TestValidator.httpError("invalid type rejected")(400)(() =>
        api.functional.communicationReview.messages.put(connection, created.id, invalidTypeInput)
    );

    // 5. Update with forbidden fields (should be ignored or unchanged)
    const forbiddenInput = {
        content: "Should not update sender or thread",
        sender_user_id: "33333333-3333-3333-3333-333333333333",
        thread_id: "44444444-4444-4444-4444-444444444444"
    } as any;
    const forbiddenUpdate: IMessage = await api.functional.communicationReview.messages.put(connection, created.id, forbiddenInput);
    typia.assert<IMessage>(forbiddenUpdate);
    TestValidator.equals("sender_user_id immutable")(created.sender_user_id)(forbiddenUpdate.sender_user_id);
    TestValidator.equals("thread_id immutable")(created.thread_id)(forbiddenUpdate.thread_id);

    // 6. Update non-existent message
    const randomNotFoundId = "99999999-8888-7777-6666-555555555555";
    await TestValidator.httpError("non-existent message", 404)(404)(() =>
        api.functional.communicationReview.messages.put(connection, randomNotFoundId, updateInput)
    );
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IMessage } from "@ORGANIZATION/PROJECT-api/lib/structures/IMessage";
import typia from "typia";

import { v4 as uuidv4 } from "uuid";

export async function test_delete_message_archive_idempotency_and_not_found(connection: api.IConnection): Promise<void> {
  // Step 1. Create a message
  const validThreadId = uuidv4();
  const createInput: IMessage.ICreate = {
    thread_id: validThreadId,
    content: "Hello for delete/archive test!",
    message_type: "user",
    parent_message_id: null,
  };
  const created: IMessage = await api.functional.communicationReview.messages.post(connection, createInput);
  typia.assert<IMessage>(created);

  // Step 2. Delete (archive) the message
  const deleted: IMessage = await api.functional.communicationReview.messages.delete(connection, created.id);
  typia.assert<IMessage>(deleted);
  TestValidator.equals("deleted message ID")<string>(created.id)(deleted.id);
  TestValidator.equals("archived true")(true)(deleted.archived);

  // Step 3. Idempotency: delete again
  const deletedAgain: IMessage = await api.functional.communicationReview.messages.delete(connection, created.id);
  typia.assert<IMessage>(deletedAgain);
  TestValidator.equals("archived after re-delete")(true)(deletedAgain.archived);
  TestValidator.equals("message ID remains")<string>(created.id)(deletedAgain.id);

  // Step 4. Try to delete a non-existent ID (expect 404 or appropriate error)
  const randomId = uuidv4();
  await TestValidator.httpError("delete non-existent message")(404)(() =>
    api.functional.communicationReview.messages.delete(connection, randomId)
  );

  // Step 5. (Optional) Would check thread listing but no API provided for listing by thread, so step skipped.
}

import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { INotification } from "@ORGANIZATION/PROJECT-api/lib/structures/INotification";
import { IPageINotification } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageINotification";
import typia from "typia";

export async function test_api_list_notifications_with_filters_and_pagination(connection: api.IConnection): Promise<void> {
    // Prepare users and thread IDs
    const userId1 = "11111111-1111-1111-1111-111111111111";
    const userId2 = "22222222-2222-2222-2222-222222222222";
    const threadId = "33333333-3333-3333-3333-333333333333";
    const now = new Date().toISOString();

    // 1. Create notifications
    const notif1 = await api.functional.communicationReview.notifications.post(connection, {
        user_id: userId1,
        notification_type: "system",
        content: "Welcome to the system!",
        thread_id: null
    });
    typia.assert<INotification>(notif1);

    const notif2 = await api.functional.communicationReview.notifications.post(connection, {
        user_id: userId2,
        notification_type: "promotion",
        content: "Discount available.",
        thread_id: threadId
    });
    typia.assert<INotification>(notif2);

    // 2. Fetch by user_id
    const respByUser: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        user_id: userId1
    });
    typia.assert<IPageINotification>(respByUser);
    TestValidator.predicate("UserId Filter")(respByUser.data.every(n => n.user_id === userId1));

    // 3. Filter by notification_type
    const respByType: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        notification_type: "promotion"
    });
    typia.assert<IPageINotification>(respByType);
    TestValidator.predicate("Type Filter")(respByType.data.every(n => n.notification_type === "promotion"));

    // 4. Filter with archived = false (should include both)
    const respUnarchived: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        archived: false
    });
    typia.assert<IPageINotification>(respUnarchived);
    // All results should have archived=false
    TestValidator.predicate("Archived=false Filter")(respUnarchived.data.every(n => n.archived === false));

    // 5. Filter by thread_id
    const respByThread: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        thread_id: threadId
    });
    typia.assert<IPageINotification>(respByThread);
    TestValidator.predicate("ThreadId Filter")(respByThread.data.every(n => n.thread_id === threadId));

    // 6. sent_after/sent_before
    const dtPast = new Date(Date.now() - 1000 * 60 * 60).toISOString();
    const dtFuture = new Date(Date.now() + 1000 * 60 * 60).toISOString();
    const respByTime: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        sent_after: dtPast,
        sent_before: dtFuture
    });
    typia.assert<IPageINotification>(respByTime);
    TestValidator.predicate("sent_after/sent_before Filter")(respByTime.data.length >= 2);

    // 7. Pagination: page=1/limit=1 should get a single notification
    const respPage1: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        page: 1,
        limit: 1
    });
    typia.assert<IPageINotification>(respPage1);
    TestValidator.equals("Single page limit")(1)(respPage1.data.length);
    TestValidator.equals("Pagination meta")(1)(respPage1.pagination.limit);

    // 8. No-match: invalid user_id
    const respNone: IPageINotification = await api.functional.communicationReview.notifications.patch(connection, {
        user_id: "00000000-0000-0000-0000-000000000000"
    });
    typia.assert<IPageINotification>(respNone);
    TestValidator.equals("No-match for bogus user")(0)(respNone.data.length);

    // 9. Structure/content guarantees
    for (const n of respByUser.data) {
        typia.assert<INotification>(n);
        TestValidator.predicate("INotification.id")(typeof n.id === "string" && n.id.length > 0);
        TestValidator.predicate("INotification.user_id")(n.user_id === userId1);
        TestValidator.predicate("INotification.sent_at")(typeof n.sent_at === "string");
    }
}

