export async function test_api_create_notification_with_valid_and_invalid_data(connection: api.IConnection): Promise<void> {
    // 1. Prepare valid data
    const validPayload: INotification.ICreate = {
        user_id: "11111111-1111-1111-1111-111111111111", // assume this is a valid uuid in test DB
        notification_type: "system",
        content: "Test system notification for E2E.",
        thread_id: null,
    };

    // 2. Create with valid payload
    const created = await api.functional.communicationReview.notifications.post(
        connection,
        validPayload
    );
    typia.assert<INotification>(created);
    TestValidator.equals("user_id")(validPayload.user_id)(created.user_id);
    TestValidator.equals("notification_type")(validPayload.notification_type)(created.notification_type);
    TestValidator.equals("content")(validPayload.content)(created.content);
    TestValidator.equals("archived")(false)(created.archived);
    TestValidator.predicate("sent_at should be ISO string")(() => typeof created.sent_at === "string" && !isNaN(Date.parse(created.sent_at)));
    TestValidator.equals("thread_id")(validPayload.thread_id)(created.thread_id);

    // 5a. Missing user_id
    const missingUser: Omit<INotification.ICreate, "user_id"> = {
        notification_type: "system",
        content: "Missing user_id field",
        thread_id: null,
    };
    await TestValidator.httpError("Missing user_id triggers error")(400)(
        () => api.functional.communicationReview.notifications.post(connection, missingUser as any)
    );

    // 5b. Missing content
    const missingContent: Omit<INotification.ICreate, "content"> = {
        user_id: validPayload.user_id,
        notification_type: "message",
        thread_id: null,
    };
    await TestValidator.httpError("Missing content should fail")(400)(
        () => api.functional.communicationReview.notifications.post(connection, missingContent as any)
    );

    // 5c. Invalid type
    const invalidTypePayload: INotification.ICreate = {
        user_id: validPayload.user_id,
        notification_type: "not-a-real-type",
        content: "Invalid notification type test",
        thread_id: null,
    };
    await TestValidator.httpError("Invalid notification_type should fail")(400, 422)(
        () => api.functional.communicationReview.notifications.post(connection, invalidTypePayload)
    );
}

