import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { INotification } from "@ORGANIZATION/PROJECT-api/lib/structures/INotification";
import typia from "typia";

export async function test_api_delete_notification_archive_idempotency_and_not_found(connection: api.IConnection): Promise<void> {
    // 1. Create a notification
    const notificationBody: INotification.ICreate = {
        user_id: "00000000-0000-4000-8000-000000000042",
        notification_type: "system",
        content: "Auto-generated test notification.",
        thread_id: null,
    };
    const created = await api.functional.communicationReview.notifications.post(connection, notificationBody);
    typia.assert<INotification>(created);
    TestValidator.equals("created.archived should be false")<boolean>(false)(created.archived);
    // 2. Delete (archive) the notification
    const deleted = await api.functional.communicationReview.notifications.delete(connection, created.id);
    typia.assert<INotification>(deleted);
    TestValidator.equals("deleted.resource.id matches")<string>(created.id)(deleted.id);
    TestValidator.equals("deleted.archived is true")<boolean>(true)(deleted.archived);
