import { TestValidator } from "@nestia/e2e";
import api from "@ORGANIZATION/PROJECT-api";
import { IReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IReview";
import typia from "typia";
import { IReview as IReviewNS } from "@ORGANIZATION/PROJECT-api/lib/structures/IReview";
import { IPageIReview } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIReview";

export async function test_api_list_reviews_with_various_filters(connection: api.IConnection): Promise<void> {
    // 1. Create a baseline review
    const baseReviewInput: IReviewNS.ICreate = {
        user_id: "00000000-0000-0000-0000-000000000001",
        product_id: "00000000-0000-0000-0000-0000000000a1",
        content: "Solid product. Works as expected.",
        language_code: "en",
        verified_purchase: true
    };
    const created: IReview = await api.functional.communicationReview.reviews.post(connection, baseReviewInput);
    typia.assert<IReview>(created);

    // 2. List all reviews (no filters)
    const listAllRes = await api.functional.communicationReview.reviews.patch(connection, {});
    typia.assert<IPageIReview>(listAllRes);
    TestValidator.predicate("at least one review exists")(() => listAllRes.data.length >= 1);
    const found = listAllRes.data.find(r => r.id === created.id);
    TestValidator.predicate("created review is in the list")(() => !!found);

    // 3. Filter by reviewer (user_id)
    const userReviews = await api.functional.communicationReview.reviews.patch(connection, {
        user_id: baseReviewInput.user_id
    });
    typia.assert<IPageIReview>(userReviews);
    TestValidator.predicate("reviews filtered by user_id exist")(() => userReviews.data.some(r => r.user_id === baseReviewInput.user_id));

    // 4. Filter by product (product_id)
    const productReviews = await api.functional.communicationReview.reviews.patch(connection, {
        product_id: baseReviewInput.product_id
    });
    typia.assert<IPageIReview>(productReviews);
    TestValidator.predicate("reviews filtered by product_id exist")(() => productReviews.data.some(r => r.product_id === baseReviewInput.product_id));

    // 5. Filter by archived (should not be archived)
    const unarchived = await api.functional.communicationReview.reviews.patch(connection, {
        archived: false
    });
    typia.assert<IPageIReview>(unarchived);
    TestValidator.predicate("unarchived reviews returned")(() => unarchived.data.every(r => !r.archived));

    // 6. Filter by language_code
    const langRes = await api.functional.communicationReview.reviews.patch(connection, {
        language_code: baseReviewInput.language_code
    });
    typia.assert<IPageIReview>(langRes);
    TestValidator.predicate("reviews filtered by language_code exist")(() => langRes.data.some(r => r.language_code === baseReviewInput.language_code));

    // 7. Filter by verified_purchase
    const verifiedRes = await api.functional.communicationReview.reviews.patch(connection, {
        verified_purchase: true
    });
    typia.assert<IPageIReview>(verifiedRes);
    TestValidator.predicate("verified purchase reviews exist")(() => verifiedRes.data.some(r => r.verified_purchase === true));

    // 8. Date range filter - created_after (should find our review)
    const afterTime = new Date(Date.parse(created.created_at) - 1000).toISOString();
    const afterRes = await api.functional.communicationReview.reviews.patch(connection, {
        created_after: afterTime
    });
    typia.assert<IPageIReview>(afterRes);
    TestValidator.predicate("reviews after created_at exist")(() => afterRes.data.some(r => r.id === created.id));

    // 9. Date range filter - created_before (should include our review)
    const beforeTime = new Date(Date.parse(created.created_at) + 1000).toISOString();
    const beforeRes = await api.functional.communicationReview.reviews.patch(connection, {
        created_before: beforeTime
    });
    typia.assert<IPageIReview>(beforeRes);
    TestValidator.predicate("reviews before created_at exist")(() => beforeRes.data.some(r => r.id === created.id));

    // 10. Extreme filter (non-existent user/product)
    const noneReviews = await api.functional.communicationReview.reviews.patch(connection, {
        user_id: "00000000-0000-0000-0000-000deadbeef",
        product_id: "00000000-0000-0000-0000-feedfacefeed"
    });
    typia.assert<IPageIReview>(noneReviews);
    TestValidator.equals("should return empty result")([] as IReview[])(noneReviews.data);

    // 11. Test pagination (limit, page)
    const paged = await api.functional.communicationReview.reviews.patch(connection, {
        limit: 1,
        page: 1
    });
    typia.assert<IPageIReview>(paged);
    TestValidator.predicate("pagination limit respected")(() => paged.data.length <= 1);
    TestValidator.predicate("pagination metadata valid")(() => paged.pagination.current === 1 && paged.pagination.limit === 1);

    // 12. Extreme paging (page way beyond last)
    const emptyPaged = await api.functional.communicationReview.reviews.patch(connection, {
        page: 999,
        limit: 10
    });
    typia.assert<IPageIReview>(emptyPaged);
    TestValidator.equals("should return empty data for non-existent page")([] as IReview[])(emptyPaged.data);
}

